import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { Elements, useStripe, useElements, CardElement } from "@stripe/react-stripe-js";
import axios from "axios";

// Replace with your Stripe Publishable Key
const stripePromise = loadStripe("your-publishable-key-here");

const CheckoutForm = ({ amount }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (!stripe || !elements) {
      setMessage("Stripe is not ready yet.");
      setLoading(false);
      return;
    }

    const cardElement = elements.getElement(CardElement);

    try {
      // Make a request to your backend to create a payment intent
      const { data } = await axios.post("http://localhost:5000/create-payment-intent", {
        amount,
      });

      const { clientSecret } = data;

      // Confirm the payment using the client secret
      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        },
      });

      if (result.error) {
        setMessage(`Payment failed: ${result.error.message}`);
      } else if (result.paymentIntent.status === "succeeded") {
        setMessage("Payment successful! Thank you for your purchase.");
      }
    } catch (error) {
      setMessage("An error occurred during the payment process. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: "400px", margin: "auto" }}>
      <CardElement />
      <button
        type="submit"
        disabled={!stripe || loading}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          backgroundColor: "#007BFF",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        {loading ? "Processing..." : `Pay $${amount}`}
      </button>
      {message && (
        <p style={{ marginTop: "15px", color: message.includes("successful") ? "green" : "red" }}>
          {message}
        </p>
      )}
    </form>
  );
};

const PaymentPage = () => {
  const location = useLocation();
  const { amount } = location.state || { amount: 0 };

  if (!amount || amount <= 0) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h1>Error</h1>
        <p>Invalid payment amount. Please go back and try again.</p>
      </div>
    );
  }

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Payment Page</h1>
      <p>You are about to pay <strong>${amount}</strong></p>
      <Elements stripe={stripePromise}>
        <CheckoutForm amount={amount} />
      </Elements>
    </div>
  );
};

export default PaymentPage;
