import React, { useEffect, useState } from 'react';
import './History.css'; 
import axios from 'axios';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    axios.get('http://localhost:8085/intake/get')
      .then(response => {
        const fetchedOrders = response.data;
        setOrders(fetchedOrders);

        axios.get('http://localhost:8085/waste-details/orders')
          .then(response => {
            const allWasteOrders = response.data;

            // Create a map of wasteId to imagePath for easy lookup
            const wasteImagesMap = {};

            allWasteOrders.forEach(wasteOrder => {
              if (wasteOrder.farmerid == parseInt(userId) && wasteOrder.status == "COLLECTED") {
                // If the wasteId is different or needs transformation, you can do that here.
                // For now, assuming wasteOrder.id corresponds to the correct imagePath for the order.
                wasteImagesMap[wasteOrder.id] = wasteOrder.imagePath;
              }
            });

            // Now map fetchedOrders with the images
            const newImages = {};

            fetchedOrders.forEach(order => {
              // Assuming wasteId is matching or requires transformation (like parsing or formatting)
              if (wasteImagesMap[order.wasteId]) {
                newImages[order.wasteId] = wasteImagesMap[order.wasteId];
              }
            });

            setImages(newImages); // Update images state once all images are mapped
          })
          .catch(error => {
            console.error('Error fetching waste details:', error);
          });
      })
      .catch(error => {
        console.error('Error fetching order data:', error);
      });
  }, [userId]);

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      <div className="card-container">
        {orders.map(order => (
          <div className="order-card" key={order.id}> 
            <div className="d-flex justify-content-center align-items-center">
              <img 
                src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"} 
                alt="Order Visual" 
                height="150px" 
                width="200px" 
              />
            </div>
            <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
            <p><strong>Place:</strong> {order.place}</p>
            <p><strong>Weight:</strong> {order.weight} kg</p>
            <p><strong>Collected By:</strong> {order.collectedBy}</p>
            <p><strong>Farmer Name:</strong> {order.farmerName}</p>
            <p><strong>Rate:</strong> ₹{order.rate}</p>
            <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
            <button type="button" className="btn btn-danger">Invoice</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default History;
