import React, { useEffect, useState } from 'react';
import './History.css'; 
import axios from 'axios';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    // Fetch order details
    axios.get('http://localhost:8085/intake/get')
      .then(response => {
        const fetchedOrders = response.data;
        setOrders(fetchedOrders);

        // Fetch images for each order based on wasteId
        fetchedOrders.forEach(order => {
          axios.get(`http://localhost:8085/waste-details/orders/${order.wasteId}`)
            .then(response => {
              const record = response.data;

              // Check if the farmerId matches the logged in userId and if the status is "COLLECTED"
              if (
                record.farmerid === parseInt(userId) && 
                record.status === "COLLECTED"
              ) {
                // Set the image path in the state
                setImages(prevImages => ({
                  ...prevImages,
                  [order.wasteId]: record.image_path // Correct image_path assignment
                }));
              }
            })
            .catch(error => {
              console.error(`Error fetching image for wasteId ${order.wasteId}:`, error);
            });
        });
      })
      .catch(error => {
        console.error('Error fetching order data:', error);
      });
  }, [userId]);

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      <div className="card-container">
        {orders.map(order => (
          <div className="order-card" key={order.id}> 
            <div className="d-flex justify-content-center align-items-center">
              <img 
                src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"} 
                alt="Order Visual" 
                height="150px" 
                width="200px" 
              />
            </div>
            <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
            <p><strong>Place:</strong> {order.place}</p>
            <p><strong>Weight:</strong> {order.weight} kg</p>
            <p><strong>Collected By:</strong> {order.collectedBy}</p>
            <p><strong>Farmer Name:</strong> {order.farmerName}</p>
            <p><strong>Rate:</strong> ₹{order.rate}</p>
            <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
            <button type="button" className="btn btn-danger">Invoice</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default History;
