import React, { useEffect, useState } from 'react';
import './History.css';
import axios from 'axios';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    async function fetchOrderData() {
      try {
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const fetchedOrders = intakeResponse.data;

        const wasteResponse = await axios.get('http://localhost:8085/waste-details/orders');
        const allWasteOrders = wasteResponse.data;

        const wasteImagesMap = {};

        allWasteOrders.forEach(wasteOrder => {
          if (wasteOrder.farmerid === parseInt(userId) && wasteOrder.status === "COLLECTED") {
            wasteImagesMap[wasteOrder.id] = wasteOrder.imagePath;
          }
        });

        const mappedImages = {};

        fetchedOrders.forEach(order => {
          if (wasteImagesMap[order.wasteId]) {
            mappedImages[order.wasteId] = wasteImagesMap[order.wasteId];
          }
        });

        setOrders(fetchedOrders);
        setImages(mappedImages);
      } catch (error) {
        console.error('Error fetching order or waste details:', error);
      }
    }

    if (userId) {
      fetchOrderData();
    }
  }, [userId]);

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      <div className="card-container">
        {orders.map(order => (
          <div className="order-card" key={order.id}>
            <div className="d-flex justify-content-center align-items-center">
              <img
                src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                alt="Order Visual"
                height="150px"
                width="200px"
              />
            </div>
            <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
            <p><strong>Place:</strong> {order.place}</p>
            <p><strong>Weight:</strong> {order.weight} kg</p>
            <p><strong>Collected By:</strong> {order.collectedBy}</p>
            <p><strong>Farmer Name:</strong> {order.farmerName}</p>
            <p><strong>Rate:</strong> ₹{order.rate}</p>
            <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
            <button type="button" className="btn btn-danger">Invoice</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default History;
