import React, { useState, useEffect } from "react";
import { Button, Row, Col, Modal, Card, Form, Alert } from "react-bootstrap";
import "./ProcessingStatus.css";

function ProcessingStatus() {
  const [requests, setRequests] = useState([]);
  const [pickupDate, setPickupDate] = useState("");
  const [additionalInfo, setAdditionalInfo] = useState("");
  const [showScheduleModal, setShowScheduleModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [rejectReason, setRejectReason] = useState("");
  const [showRejectModal, setShowRejectModal] = useState(false);
  const [statusUpdateMessage, setStatusUpdateMessage] = useState("");

  // Fetch data from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:8085/waste-details/orders");
        const data = await response.json();
        setRequests(data); // Assuming the response is an array
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  // Update the status of the request and send additional data (message)
  const updateRequestStatus = async (requestId, status, additionalData = {}) => {
    try {
      const response = await fetch(`http://localhost:8085/waste-details/update/${requestId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          status,
          message: additionalData.message,  // Send additional info as message
          ...additionalData, // Include other data like pickupDate if present
        }),
      });
      if (response.ok) {
        setRequests((prevRequests) =>
          prevRequests.map((request) =>
            request.id === requestId ? { ...request, status, message: additionalData.message, ...additionalData } : request
          )
        );
        setStatusUpdateMessage(`Request successfully marked as ${status}`);
      } else {
        console.error("Error updating status");
      }
    } catch (error) {
      console.error("Error updating status:", error);
      setStatusUpdateMessage("Error updating request status.");
    }
  };

  const handleConfirmRequest = (requestId) => {
    // After clicking on "Confirm", schedule the pickup and show the modal
    setSelectedRequest(requestId);
    setShowScheduleModal(true); // Show the pickup schedule form
    updateRequestStatus(requestId, "ACCEPTED", { message: "Pickup confirmed" });
  };

  const handleRejectRequest = (requestId) => {
    setSelectedRequest(requestId);
    setShowRejectModal(true);
  };

  const handleRejectSubmit = () => {
    updateRequestStatus(selectedRequest, "REJECTED", { message: rejectReason });
    setShowRejectModal(false);
    setRejectReason("");
  };

  const handleScheduleSubmit = () => {
    // Update status to pickupScheduled with pickup date and message
    updateRequestStatus(selectedRequest, "pickupScheduled", { pickupDate, message: additionalInfo });
    setShowScheduleModal(false); // Close the modal after scheduling
    setPickupDate("");
    setAdditionalInfo("");
  };

  return (
    <div className="container">
      <div className="processing-status-container">
        <h1>Processing Status</h1>
        <p>Track the status of ongoing waste processing.</p>

        {statusUpdateMessage && <Alert variant="info">{statusUpdateMessage}</Alert>}

        <h3>Waste Requests</h3>
        <div className="requests-list">
          {requests.length === 0 ? (
            <p>No requests available</p>
          ) : (
            requests.map((request) => (
              <Card key={request.id} className="request-card mb-3">
                <Card.Img
                  variant="top"
                  src={request.imagePath}
                  alt={`${request.wasteType} image`}
                  className="card-image p-2 rounded-4"
                />
                <Card.Body>
                  <Row>
                    <Col md={6}>
                      <p><strong>Name:</strong> {request.name}</p>
                      <p><strong>Waste Type:</strong> {request.wasteType}</p>
                      <p><strong>Address:</strong> {request.address}</p>
                      <p><strong>Date Created:</strong> {new Date(request.createdAt).toLocaleDateString()}</p>
                    </Col>
                    <Col md={6}>
                      {request.status === null && (
                        <>
                          <Button
                            variant="success"
                            onClick={() => handleConfirmRequest(request.id)}
                            className="mr-2"
                          >
                            Confirm
                          </Button>
                          <Button
                            variant="danger"
                            onClick={() => handleRejectRequest(request.id)}
                          >
                            Reject
                          </Button>
                        </>
                      )}
                      {request.status === "ACCEPTED" && (
                        <>
                          <p>Pickup Scheduled for: {request.pickupDate || "Not Scheduled"}</p>
                          <Button
                            variant="primary"
                            onClick={() => handleSchedulePickup(request.id)}
                            className="mt-2"
                          >
                            Schedule Pickup
                          </Button>
                        </>
                      )}
                      {request.status === "pickupScheduled" && (
                        <p>
                          Pickup scheduled for: {request.pickupDate}<br />
                          <strong>Additional Info:</strong> {request.message}
                        </p>
                      )}
                      {request.status === "REJECTED" && (
                        <p><strong>Reason for Rejection:</strong> {request.message}</p>
                      )}
                    </Col>
                  </Row>
                </Card.Body>
              </Card>
            ))
          )}
        </div>
      </div>

      {/* Reject Reason Modal */}
      <Modal show={showRejectModal} onHide={() => setShowRejectModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Reject Reason</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Provide a reason for rejection:</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={rejectReason}
              onChange={(e) => setRejectReason(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowRejectModal(false)}>
            Close
          </Button>
          <Button variant="danger" onClick={handleRejectSubmit}>
            Submit Rejection
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Schedule Pickup Modal */}
      <Modal show={showScheduleModal} onHide={() => setShowScheduleModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Schedule Pickup</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Pickup Date:</Form.Label>
            <Form.Control
              type="date"
              value={pickupDate}
              onChange={(e) => setPickupDate(e.target.value)}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Additional Information:</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={additionalInfo}
              onChange={(e) => setAdditionalInfo(e.target.value)}
              placeholder="Enter any additional info"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowScheduleModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleScheduleSubmit}>
            Schedule Pickup  
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ProcessingStatus;
