import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import './History.css';

function MyOrders() {
  const [payments, setPayments] = useState([]);
  const userId = localStorage.getItem('userId'); // Get the user ID from localStorage

  useEffect(() => {
    async function fetchPayments() {
      try {
        if (!userId) {
          console.log("No User ID found in localStorage");
          return;
        }

        // Fetching payment data
        const paymentResponse = await axios.get('http://localhost:8085/api/payment/getall');
        console.log("Payment Response: ", paymentResponse.data); // Log response

        // Filter payments where farmerid matches userId
        const filteredPayments = paymentResponse.data.filter(payment => 
          payment.sellWaste.farmerid === parseInt(userId)
        );
        
        setPayments(filteredPayments);
      } catch (error) {
        console.error('Error fetching payment data:', error);
      }
    }

    if (userId) {
      fetchPayments();
    }
  }, [userId]);

  const generateInvoice = (payment) => {
    const doc = new jsPDF();

    doc.setFontSize(22);
    doc.text('FarmEco Invoice', 105, 20, { align: 'center' });
    doc.setFontSize(12);
    doc.text('Waste Selling Company', 105, 28, { align: 'center' });
    doc.line(10, 35, 200, 35);

    doc.setFontSize(10);
    doc.text('FarmEco Pvt Ltd.', 10, 45);
    doc.text('123 Greenfield Avenue, Pune, Maharashtra', 10, 50);
    doc.text('Contact: +91-1234567890 | Email: support@farmeco.com', 10, 55);

    const invoiceNumber = `FEC-${payment.id.toString().padStart(6, '0')}-${payment.orderId}`;
    doc.setFontSize(12);
    doc.text(`Invoice Number: ${invoiceNumber}`, 140, 45);
    doc.text(`Date: ${new Date().toLocaleDateString()}`, 140, 50);

    doc.autoTable({
      startY: 70,
      head: [['Field', 'Value']],
      body: [
        ['Farmer Name:', payment.sellWaste.name],
        ['Place:', payment.sellWaste.address],
        ['Waste Type:', payment.sellWaste.wasteType],
        ['Mobile No:', payment.sellWaste.mobileNo],
      ],
      theme: 'striped',
    });

    doc.autoTable({
      startY: doc.lastAutoTable.finalY + 15,
      head: [['Item Description', 'Amount (₹)']],
      body: [['Agricultural Waste', payment.amount]],
      theme: 'grid',
    });

    doc.save(`Invoice_${invoiceNumber}.pdf`);
  };

  return (
    <div className="order-history">
      <h2>Farmer Payment History</h2>
      <div className="card-container">
        {payments.map(payment => (
          <div className="order-card" key={payment.id}>
            <img
              src={`http://localhost:8085/uploads/${payment.sellWaste.imagePath}`}
              alt="Waste Visual"
              height="150px"
              width="200px"
            />
            <p><strong>Order ID:</strong> {payment.orderId}</p>
            <p><strong>Payment ID:</strong> {payment.paymentId}</p>
            <p><strong>Farmer Name:</strong> {payment.sellWaste.name}</p>
            <p><strong>Amount:</strong> ₹{payment.amount}</p>
            <p><strong>Status:</strong> {payment.status}</p>
            <button className="btn btn-danger" onClick={() => generateInvoice(payment)}>Generate Invoice</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MyOrders;
