import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ProductListing.css";

const ProductListing = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [ordersResponse, intakeResponse] = await Promise.all([
          axios.get("http://localhost:8085/waste-details/orders"),
          axios.get("http://localhost:8085/intake/get"),
        ]);

        const orders = ordersResponse.data;
        const intakeData = intakeResponse.data;

        // Match orders with intake by wasteId
        const combinedData = intakeData.map((intakeItem) => {
          const matchingOrder = orders.find(
            (order) => order.id === intakeItem.wasteId
          );
          return {
            ...intakeItem,
            imagePath: matchingOrder ? matchingOrder.imagePath : null,
          };
        });

        setProducts(combinedData);
      } catch (err) {
        setError("Failed to fetch products derived from waste intake.");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="products-container">
      <hr />
      <h1 className="products-header text-center">Products Derived from Waste Intake</h1>
      {loading && <p className="loading-message text-center">Loading products...</p>}
      {error && <p className="error-message text-center">{error}</p>}

      <div className="products-grid">
        {products.length > 0 ? (
          products.map((product) => (
            <div className="product-card-wrapper" key={product.id}>
              <div className="product-card">
                {product.imagePath ? (
                  <img
                    src={product.imagePath}
                    className="product-image"
                    alt={`Waste product with ID ${product.id}`}
                  />
                ) : (
                  <p className="no-image-text">No Image Available</p>
                )}
                <div className="product-details">
                  <p className="product-info">
                    <b>Stock ID:</b> {product.id} <br />
                    <b>Waste Type:</b> {product.wasteType || "N/A"} <br />
                    <b>Weight:</b> {product.weight} ton
                  </p>
                </div>
              </div>
            </div>
          ))
        ) : (
          !loading && (
            <p className="no-products-message text-center">
              No products derived from waste intake are available.
            </p>
          )
        )}
      </div>
    </div>
  );
};

export default ProductListing;
