import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import Head from "./Head";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "./header.css";

const Header = () => {
  const [click, setClick] = useState(false);
  const location = useLocation();


  const isLoginPage = location.pathname === "/logins";
  const isSignPage = location.pathname === "/signup";
  const isAdminPage = location.pathname === "/adminlogin";
  const isEmployeePage = location.pathname === "/employeelogin";


  if (isLoginPage || isSignPage ||isAdminPage||isEmployeePage) {
    return null;
  }
  const hideHeader = location.state?.hideHeader || false;

  if (hideHeader) {
    return null;
  }

  return (
    <>
      <Head />
      <header>
        <nav className="flexSB">
          <ul
            className={click ? "mobile-nav" : "flexSB"}
            onClick={() => setClick(false)}
          >
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/sellwaste">Sell Waste</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/pricing">Pricing</Link>
            </li>
            <li>
              <Link to="/journal">Journal</Link>
            </li>
            <li>
              <Link to="/services">Services</Link>
            </li>
            <li>
              <Link to="/contact">Contact</Link>
            </li>
            <li className="nav-item dropdown drop">
              <a
                className="nav-link dropdown-toggle dx"
                href="Login"
                role="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                Login
              </a>
              <ul className="dropdown-menu">
                <li>
                  <Link className="dropdown-item" to="/adminlogin">
                    Admin
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-item" to="/employeelogin">
                    Employee
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-item" to="/logins">
                    User
                  </Link>
                </li>
              </ul>
            </li>
          </ul>
          <button className="toggle" onClick={() => setClick(!click)}>
            {click ? (
              <i className="fa fa-times"> </i>
            ) : (
              <i className="fa fa-bars"></i>
            )}
          </button>
        </nav>
      </header>
    </>
  );
};

export default Header;
