import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ProductListings.module.css";

const ProductListing = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    type: "",
    quantity: 0,
    price: 0,
  });

  // Fetch products from the database
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/api/products"); // Replace with your API endpoint
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  // Handle input change for new product form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle adding a new product
  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/api/products", newProduct); // Replace with your API endpoint
      setProducts((prevProducts) => [...prevProducts, response.data]);
      setNewProduct({ name: "", type: "", quantity: 0, price: 0 }); // Reset form
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  return (
    <div className="product-listings-container">
      <h1>Product Listings</h1>

      {/* Display the list of products */}
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <p>Type: {product.type}</p>
            <p>Quantity: {product.quantity}</p>
            <p>Price: ${product.price}</p>
          </div>
        ))}
      </div>

      {/* Form to add a new product */}
      <form className="add-product-form" onSubmit={handleAddProduct}>
        <h2>Add a New Product</h2>
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="type"
          placeholder="Product Type"
          value={newProduct.type}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={newProduct.quantity}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={newProduct.price}
          onChange={handleInputChange}
          required
        />
        <button type="submit">Add Product</button>
      </form>
    </div>
  );
};

export default ProductListing;
