import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Dashboard.css";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [metrics, setMetrics] = useState({
    totalWasteProcessed: 0,
    totalWasteRemaining: 0,
    totalCyclesCompleted: 0,
    availableProducts: 0,
    wasteIntakeData: [],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/api/metrics"); // Replace with your API endpoint
        setMetrics(response.data);
      } catch (error) {
        console.error("Error fetching dashboard metrics:", error);
      }
    };
    fetchData();
  }, []);

  const chartData = {
    labels: metrics.wasteIntakeData.map(item => item.date),
    datasets: [
      {
        label: "Waste Intake Over Time",
        data: metrics.wasteIntakeData.map(item => item.volume),
        borderColor: "#3b82f6",
        backgroundColor: "rgba(59, 130, 246, 0.2)",
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <h1>Waste Processing Dashboard</h1>
      <div className="dashboard-metrics">
        <div className="metric-card">
          <h2>Total Waste Processed</h2>
          <p>{metrics.totalWasteProcessed} tons</p>
        </div>
        <div className="metric-card">
          <h2>Total Waste Remaining</h2>
          <p>{metrics.totalWasteRemaining} tons</p>
        </div>
        <div className="metric-card">
          <h2>Completed Processing Cycles</h2>
          <p>{metrics.totalCyclesCompleted}</p>
        </div>
        <div className="metric-card">
          <h2>Available Products</h2>
          <p>{metrics.availableProducts} units</p>
        </div>
      </div>

      <div className="metrics-visualization">
        <h2>Waste Intake Over Time</h2>
        <Line data={chartData} />
      </div>

      <div className="dashboard-navigation">
        <button className="nav-button">Go to Waste Intake</button>
        <button className="nav-button">Go to Processing Status</button>
        <button className="nav-button">View Product Listings</button>
        <button className="nav-button">View Reports</button>
      </div>
    </div>
  );
};

export default Dashboard;
