import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import './History.css';

function History() {
  const [orders, setOrders] = useState([]);
  const [rejectedOrders, setRejectedOrders] = useState([]);
  const [images, setImages] = useState({});
  const [showCollected, setShowCollected] = useState(true);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    async function fetchData() {
      try {
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const fetchedOrders = intakeResponse.data;

        const wasteResponse = await axios.get(`http://localhost:8085/waste-details/orders/${userId}`);
        const wasteOrders = wasteResponse.data.filter(order => order.status === "COLLECTED" || order.status === "REJECTED");

        const wasteImagesMap = {};
        wasteOrders.forEach(order => {
          wasteImagesMap[order.id] = order.imagePath;
        });

        const matchedOrders = fetchedOrders.filter(order => wasteImagesMap[order.wasteId]);
        setOrders(matchedOrders.filter(order => order.status === "COLLECTED"));
        setRejectedOrders(matchedOrders.filter(order => order.status === "REJECTED"));
        setImages(wasteImagesMap);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    if (userId) {
      fetchData();
    }
  }, [userId]);

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>

      <div className="button-group">
        <button
          type="button"
          className={`btn ${showCollected ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setShowCollected(true)}
        >
          Show Collected Orders
        </button>

        <button
          type="button"
          className={`btn ${!showCollected ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setShowCollected(false)}
        >
          Show Rejected Orders
        </button>
      </div>

      {showCollected ? (
        <div className="card-container">
          {orders.length > 0 ? (
            orders.map(order => (
              <div className="order-card" key={order.id}>
                <img
                  src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                  alt="Order Visual"
                  height="150px"
                  width="200px"
                />
                <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
                <p><strong>Place:</strong> {order.place}</p>
                <p><strong>Weight:</strong> {order.weight} kg</p>
                <p><strong>Collected By:</strong> {order.collectedBy}</p>
                <p><strong>Farmer Name:</strong> {order.farmerName}</p>
                <p><strong>Rate:</strong> ₹{order.rate}</p>
                <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
              </div>
            ))
          ) : (
            <p>No collected orders available.</p>
          )}
        </div>
      ) : (
        <div className="card-container">
          {rejectedOrders.length > 0 ? (
            rejectedOrders.map(order => (
              <div className="order-card" key={order.id}>
                <img
                  src={images[order.id] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                  alt={order.wasteType || "Product"}
                  height="150px"
                  width="200px"
                />
                <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
                <p><strong>Place:</strong> {order.place}</p>
                <p><strong>Status:</strong> {order.status}</p>
              </div>
            ))
          ) : (
            <p>No rejected orders available.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default History;
