import React, { useState, useEffect } from "react";
import { Button, Card, Modal, Form, Row, Col } from "react-bootstrap";
import "./ProcessingStatus.css";

function ProcessingStatus() {
  const [requests, setRequests] = useState([]);
  const [pickupDate, setPickupDate] = useState("");
  const [driverName, setDriverName] = useState("");
  const [additionalInfo, setAdditionalInfo] = useState("");
  const [showScheduleModal, setShowScheduleModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [rejectReason, setRejectReason] = useState("");
  const [showRejectModal, setShowRejectModal] = useState(false);

  useEffect(() => {
    const mockRequests = [
      {
        id: 1,
        type: "Plastic",
        volume: 5,
        source: "Green Valley Farms",
        date: "2024-12-10",
        status: "pending",
        image: "https://via.placeholder.com/150",
      },
      {
        id: 2,
        type: "Organic",
        volume: 3,
        source: "Sunnydale Orchards",
        date: "2024-12-12",
        status: "pending",
        image: "https://via.placeholder.com/150",
      },
      {
        id: 3,
        type: "Glass",
        volume: 2,
        source: "Evergreen Plantation",
        date: "2024-12-11",
        status: "confirmed",
        image: "https://via.placeholder.com/150",
      },
    ];
    setRequests(mockRequests);
  }, []);

  const handleConfirmRequest = (requestId) => {
    setRequests((prevRequests) =>
      prevRequests.map((request) =>
        request.id === requestId
          ? { ...request, status: "confirmed" }
          : request
      )
    );
  };

  const handleRejectRequest = (requestId) => {
    setSelectedRequest(requestId);
    setShowRejectModal(true);
  };

  const handleRejectSubmit = () => {
    setRequests((prevRequests) =>
      prevRequests.map((request) =>
        request.id === selectedRequest
          ? { ...request, status: "rejected", rejectReason }
          : request
      )
    );
    setShowRejectModal(false);
    setRejectReason("");
  };

  const handleSchedulePickup = (requestId) => {
    setSelectedRequest(requestId);
    setShowScheduleModal(true);
  };

  const handleScheduleSubmit = () => {
    setRequests((prevRequests) =>
      prevRequests.map((request) =>
        request.id === selectedRequest
          ? {
              ...request,
              status: "pickupScheduled",
              pickupDate,
              driverName,
              additionalInfo,
            }
          : request
      )
    );
    setShowScheduleModal(false);
    setPickupDate("");
    setDriverName("");
    setAdditionalInfo("");
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Processing Status</h1>
      <Row>
        {requests.map((request) => (
          <Col key={request.id} md={4} className="mb-4">
            <Card className="h-100">
              <Card.Img
                variant="top"
                src={request.image}
                alt={`${request.type} image`}
              />
              <Card.Body>
                <Card.Title>{request.type} Waste</Card.Title>
                <Card.Text>
                  <strong>Volume:</strong> {request.volume} tons<br />
                  <strong>Source:</strong> {request.source}<br />
                  <strong>Date:</strong> {request.date}<br />
                  <strong>Status:</strong> {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                </Card.Text>
                {request.status === "pending" && (
                  <>
                    <Button
                      variant="success"
                      onClick={() => handleConfirmRequest(request.id)}
                      className="me-2"
                    >
                      Confirm
                    </Button>
                    <Button
                      variant="danger"
                      onClick={() => handleRejectRequest(request.id)}
                    >
                      Reject
                    </Button>
                  </>
                )}
                {request.status === "confirmed" && (
                  <Button
                    variant="primary"
                    onClick={() => handleSchedulePickup(request.id)}
                  >
                    Schedule Pickup
                  </Button>
                )}
                {request.status === "pickupScheduled" && (
                  <Card.Text>
                    <strong>Pickup Date:</strong> {request.pickupDate}<br />
                    <strong>Driver:</strong> {request.driverName}<br />
                    <strong>Info:</strong> {request.additionalInfo}
                  </Card.Text>
                )}
                {request.status === "rejected" && (
                  <Card.Text className="text-danger">
                    <strong>Rejection Reason:</strong> {request.rejectReason}
                  </Card.Text>
                )}
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Reject Modal */}
      <Modal show={showRejectModal} onHide={() => setShowRejectModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Reject Request</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Reason for Rejection:</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={rejectReason}
              onChange={(e) => setRejectReason(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowRejectModal(false)}
          >
            Close
          </Button>
          <Button variant="danger" onClick={handleRejectSubmit}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Schedule Pickup Modal */}
      <Modal show={showScheduleModal} onHide={() => setShowScheduleModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Schedule Pickup</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Pickup Date:</Form.Label>
            <Form.Control
              type="date"
              value={pickupDate}
              onChange={(e) => setPickupDate(e.target.value)}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Driver Name:</Form.Label>
            <Form.Control
              type="text"
              value={driverName}
              onChange={(e) => setDriverName(e.target.value)}
              placeholder="Enter driver's name"
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Additional Info:</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={additionalInfo}
              onChange={(e) => setAdditionalInfo(e.target.value)}
              placeholder="Enter additional info"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowScheduleModal(false)}
          >
            Close
          </Button>
          <Button variant="primary" onClick={handleScheduleSubmit}>
            Schedule
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ProcessingStatus;
