import React, { useRef, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import "./Login.css";

export default function AdminLogin() {
    const admin = useRef();
    const pass = useRef();
    const [error, setError] = useState("");
    const navigate = useNavigate();
    const location = useLocation();

    const handleLogin = () => {
        const adminVal = admin.current.value;
        const passVal = pass.current.value;

        if (adminVal === "admin" && passVal === "password") {
            var admindashboard = location.pathname === "/admindash";
            if(!admindashboard){
                return null;
            }
        } else {
            setError("Please enter valid credentials");
        }
    };

    return (
        <div className="unique-login-page">
            <div className="unique-login-container">
                <div className="unique-form-container">
                    <Link to="/about" className="signup-link">Back</Link>
                    <h1 className="unique-form-title">Admin</h1>
                    <h2 className="unique-form-title">Sign In</h2>
                    <form className="unique-form unique-signin-form">
                        <div className="unique-form-group">
                            <label htmlFor="username">Username</label>
                            <input
                                className="unique-form-input"
                                type="text"
                                name="username"
                                placeholder="Enter your username"
                                ref={admin}
                            />
                        </div>
                        <div className="unique-form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                className="unique-form-input"
                                type="password"
                                name="password"
                                placeholder="Enter your password"
                                ref={pass}
                            />
                        </div>
                        <div className="unique-form-options">
                            <label className="unique-checkbox-label">
                                <input type="checkbox" id="keepSignedIn" />
                                Keep me signed in
                            </label>
                        </div>
                        <button
                            type="button"
                            className="unique-form-submit-button"
                            onClick={handleLogin}
                        >
                            Sign In
                        </button>
                    </form>
                    {error && <p className="error-message">{error}</p>} 
                </div>
            </div>
        </div>
    );
}
