import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
// import { toast } from "react-toastify";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1);

  const handleForgotPassword = async () => {
    if (!email || !newPassword) {
      toast.error("Please provide both email and new password.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8085/api/farmers/forgot-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, newPassword }),
      });

      const data = await response.json();
      if (response.ok) {
        toast.success(data.message);
        setStep(2);
      } else {
        toast.error(data.message);
      }
    } catch (error) {
      toast.error("Something went wrong!");
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      toast.error("Please enter the OTP.");
      return;
    }
   
    try {
      const response = await fetch("http://localhost:8085/api/farmers/verify", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, otp }),
      });

      const data = await response.json();
      if (response.ok) {
        toast.success(data.message);
        setStep(1);
      } else {
        toast.error(data.message);
      }
    } catch (error) {
      toast.error("Verification failed.");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen">
      <Card className="p-8 w-full max-w-md shadow-xl rounded-2xl">
        <CardContent>
          <h1 className="text-2xl font-bold mb-4">
            {step === 1 ? "Forgot Password" : "Verify OTP"}
          </h1>

          {step === 1 ? (
            <>
              <Input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="mb-4"
              />
              <Input
                type="password"
                placeholder="Enter new password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="mb-4"
              />
              <Button onClick={handleForgotPassword} className="w-full">
                Submit
              </Button>
            </>
          ) : (
            <>
              <Input
                type="text"
                placeholder="Enter OTP"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                className="mb-4"
              />
              <Button onClick={handleVerifyOtp} className="w-full">
                Verify OTP
              </Button>
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default ForgotPassword;
