import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import './History.css';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const [filter, setFilter] = useState('ALL');
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    async function fetchData() {
      try {
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const fetchedOrders = intakeResponse.data;

        const wasteResponse = await axios.get(`http://localhost:8085/waste-order/${userId}`);
        const wasteOrders = wasteResponse.data.filter(order => order.status === 'COLLECTED' || order.status === 'REJECTED');

        const wasteImagesMap = {};
        wasteOrders.forEach(order => {
          wasteImagesMap[order.id] = order.imagePath;
        });

        const matchedOrders = fetchedOrders.filter(order => wasteImagesMap[order.wasteId]);
        setOrders(matchedOrders);
        setImages(wasteImagesMap);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    if (userId) {
      fetchData();
    }
  }, [userId]);

  const filteredOrders = filter === 'ALL' ? orders : orders.filter(order => order.status === filter);

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      
      <div className="filter-container">
        <button onClick={() => setFilter('ALL')} className={filter === 'ALL' ? 'active' : ''}>All</button>
        <button onClick={() => setFilter('COLLECTED')} className={filter === 'COLLECTED' ? 'active' : ''}>Collected</button>
        <button onClick={() => setFilter('REJECTED')} className={filter === 'REJECTED' ? 'active' : ''}>Rejected</button>
      </div>
      
      <div className="card-container">
        {filter === 'COMPLETED' ? (
          filteredOrders.map(order => (
            <div className="order-card" key={order.id}>
              <div className="d-flex justify-content-center align-items-center">
                <img
                  src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                  alt="Order Visual"
                  height="150px"
                  width="200px"
                />
              </div>
              <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
              <p><strong>Place:</strong> {order.place}</p>
              <p><strong>Weight:</strong> {order.weight} kg</p>
              <p><strong>Collected By:</strong> {order.collectedBy}</p>
              <p><strong>Farmer Name:</strong> {order.farmerName}</p>
              <p><strong>Rate:</strong> ₹{order.rate}</p>
              <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
            </div>
          ))
        ) : (
          <section className="order-status">
            <h3>Your Orders</h3>
            <div className="card-container">
              {filteredOrders.length > 0 ? (
                filteredOrders.map((order) => (
                  <div
                    key={order.id}
                    className={`card ${order.status ? order.status.toLowerCase() : "pending"}`}
                  >
                    <h4>Order ID: ORD-{order.id}</h4>
                    <div className="d-flex justify-content-center align-items-center my-2">
                      <img
                        src={order.imagePath}
                        height="150px"
                        width="200px"
                        alt={order.wasteType || "Product"}
                        onError={(e) => {
                          console.error("Image not found:", order.imagePath);
                          e.target.src = "https://via.placeholder.com/200x150?text=No+Image+Available";
                        }}
                      />
                    </div>
                    <p>Waste Type: {order.wasteType || "Not specified"}</p>
                    <p>
                      Status: <span className="order-status-text">{order.status || "Pending"}</span>
                    </p>
                    <button className="track-button" onClick={() => openTrackOrder(order)}>
                      Track Order
                    </button>
                  </div>
                ))
              ) : (
                <p>No orders available.</p>
              )}
            </div>
          </section>
        )}
      </div>
    </div>
  );
}

