import React, { useState, useEffect } from "react";
import { Line, Pie } from "react-chartjs-2";
// import "./Dashboard.module.css";

const Dashboard = () => {
  const [metrics, setMetrics] = useState({
    totalWasteProcessed: 0,
    totalWasteRemaining: 0,
    totalCyclesCompleted: 0,
    availableProducts: 0,
    wasteIntakeData: [],
    wasteComposition: {
      compost: 0,
      biogas: 0,
      recycling: 0,
    },
    recentViolations: [],
    enforcementActions: [],
  });

  useEffect(() => {
    // Dummy data to simulate the fetch from an API
    const fetchData = () => {
      const data = {
        totalWasteProcessed: 1200,
        totalWasteRemaining: 300,
        totalCyclesCompleted: 15,
        availableProducts: 500,
        wasteIntakeData: [
          { date: "2024-01-01", volume: 100 },
          { date: "2024-01-02", volume: 150 },
          { date: "2024-01-03", volume: 200 },
          { date: "2024-01-04", volume: 180 },
          { date: "2024-01-05", volume: 210 },
          { date: "2024-01-06", volume: 250 },
        ],
        wasteComposition: {
          compost: 600,
          biogas: 400,
          recycling: 200,
        },
        recentViolations: [
          { facility: "Facility A", violation: "Improper waste storage", date: "2024-01-05" },
          { facility: "Facility B", violation: "Missing documentation", date: "2024-01-03" },
        ],
        enforcementActions: [
          { facility: "Facility C", action: "Fine imposed", date: "2024-01-01", amount: "$5,000" },
          { facility: "Facility D", action: "Compliance order issued", date: "2024-01-02" },
        ],
      };
      setMetrics(data);
    };
    fetchData();
  }, []);

  const lineChartData = {
    labels: metrics.wasteIntakeData.map(item => item.date),
    datasets: [
      {
        label: "Waste Intake Over Time (tons)",
        data: metrics.wasteIntakeData.map(item => item.volume),
        borderColor: "#4CAF50",
        backgroundColor: "rgba(76, 175, 80, 0.2)",
        tension: 0.4,
      },
    ],
  };

  const pieChartData = {
    labels: ["Compost", "Biogas", "Recycling"],
    datasets: [
      {
        data: [
          metrics.wasteComposition.compost,
          metrics.wasteComposition.biogas,
          metrics.wasteComposition.recycling,
        ],
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
        hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">Waste Processing Dashboard</h1>
      <div className="dashboard-metrics">
        <div className="metric-card">
          <h2>Total Waste Processed</h2>
          <p>{metrics.totalWasteProcessed} tons</p>
        </div>
        <div className="metric-card">
          <h2>Total Waste Remaining</h2>
          <p>{metrics.totalWasteRemaining} tons</p>
        </div>
        <div className="metric-card">
          <h2>Completed Processing Cycles</h2>
          <p>{metrics.totalCyclesCompleted}</p>
        </div>
        <div className="metric-card">
          <h2>Available Products</h2>
          <p>{metrics.availableProducts}</p>
        </div>
      </div>
      <div className="charts-section">
        <h2>Waste Intake Over Time</h2>
        <Line data={lineChartData} />
      </div>
      <div className="charts-section">
        <h2>Waste Composition</h2>
        <Pie data={pieChartData} />
      </div>
      <div className="violations-section">
        <h2>Recent Violations</h2>
        <ul>
          {metrics.recentViolations.map((violation, index) => (
            <li key={index}>
              {violation.date}: {violation.facility} - {violation.violation}
            </li>
          ))}
        </ul>
      </div>
      <div className="enforcement-actions-section">
        <h2>Enforcement Actions</h2>
        <ul>
          {metrics.enforcementActions.map((action, index) => (
            <li key={index}>
              {action.date}: {action.facility} - {action.action} {action.amount ? `(${action.amount})` : ''}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
