import React, { useEffect, useState } from 'react';
import './History.css';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    async function fetchData() {
      try {
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const fetchedOrders = intakeResponse.data;

        const wasteResponse = await axios.get(`http://localhost:8085/waste-details/orders/${userId}`);
        const wasteOrders = wasteResponse.data.filter(order => order.status === "COLLECTED");

        const wasteImagesMap = {};
        wasteOrders.forEach(order => {
          wasteImagesMap[order.id] = order.imagePath;
        });

        const matchedOrders = fetchedOrders.filter(order => wasteImagesMap[order.wasteId]);
        setOrders(matchedOrders);
        setImages(wasteImagesMap);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    if (userId) {
      fetchData();
    }
  }, [userId]);

  const generateInvoice = (order) => {
    const doc = new jsPDF();
    
    // Header
    doc.setFontSize(18);
    doc.text('Farmer Order Invoice', 105, 15, { align: 'center' });
    doc.setFontSize(12);
    doc.text('Generated by Farming Marketplace System', 105, 25, { align: 'center' });
    doc.line(10, 30, 200, 30); // Horizontal line

    // Order Info Section
    doc.setFontSize(14);
    doc.text('Order Details', 10, 40);

    const orderData = [
      ['Order ID', order.id],
      ['Order Date', new Date(order.collectedDate).toLocaleDateString()],
      ['Place', order.place],
      ['Weight (kg)', `${order.weight} kg`],
      ['Collected By', order.collectedBy],
      ['Farmer Name', order.farmerName],
      ['Rate per kg', `₹${order.rate}`],
      ['Total Price', `₹${order.totalPrice}`],
    ];

    // Auto Table for Order Info
    doc.autoTable({
      startY: 45,
      head: [['Field', 'Value']],
      body: orderData,
      theme: 'grid',
      styles: { halign: 'left' },
      columnStyles: { 0: { fontStyle: 'bold' } },
    });

    // Footer Info
    doc.setFontSize(10);
    doc.text('Thank you for partnering with us!', 105, doc.lastAutoTable.finalY + 15, {
      align: 'center',
    });

    // Save PDF
    doc.save(`Invoice_Order_${order.id}.pdf`);
  };

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      <div className="card-container">
        {orders.map(order => (
          <div className="order-card" key={order.id}>
            <div className="d-flex justify-content-center align-items-center">
              <img
                src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                alt="Order Visual"
                height="150px"
                width="200px"
              />
            </div>
            <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
            <p><strong>Place:</strong> {order.place}</p>
            <p><strong>Weight:</strong> {order.weight} kg</p>
            <p><strong>Collected By:</strong> {order.collectedBy}</p>
            <p><strong>Farmer Name:</strong> {order.farmerName}</p>
            <p><strong>Rate:</strong> ₹{order.rate}</p>
            <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
            <button
              type="button"
              className="btn btn-danger"
              onClick={() => generateInvoice(order)}
            >
              Generate Invoice
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default History;
