import { useState, useEffect } from "react";

const ForgotPassword = () => {
  const [email, setEmail] = useState(localStorage.getItem("email") || "");
  const [newPassword, setNewPassword] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(parseInt(localStorage.getItem("step")) || 1);

  useEffect(() => {
    localStorage.setItem("email", email);
    localStorage.setItem("step", step);
  }, [email, step]);

  const handleCheckEmail = async () => {
    if (!email) {
      alert("Please enter your email.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8085/api/farmers/check-email", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("OTP sent to your email.");
        setStep(2);
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert("Error validating email.");
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8085/api/farmers/verify-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, otp }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("OTP verified. Set your new password.");
        setStep(3);
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert("OTP verification failed.");
    }
  };

  const handleUpdatePassword = async () => {
    if (!newPassword) {
      alert("Please enter a new password.");
      return;
    }

    try {
      const response = await fetch("http://localhost:8085/api/farmers/update-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, newPassword }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Password updated successfully.");
        setStep(1);
        localStorage.clear();
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert("Password update failed.");
    }
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
      <div style={{ width: "300px", padding: "20px", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)" }}>
        <h2>
          {step === 1 ? "Forgot Password" : step === 2 ? "Verify OTP" : "Set New Password"}
        </h2>

        {step === 1 && (
          <>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <button onClick={handleCheckEmail} style={{ width: "100%" }}>
              Submit
            </button>
          </>
        )}

        {step === 2 && (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <button onClick={handleVerifyOtp} style={{ width: "100%" }}>
              Verify OTP
            </button>
          </>
        )}

        {step === 3 && (
          <>
            <input
              type="password"
              placeholder="Enter new password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              style={{ display: "block", width: "100%", marginBottom: "10px" }}
            />
            <button onClick={handleUpdatePassword} style={{ width: "100%" }}>
              Update Password
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default ForgotPassword;
