import React, { useState, useEffect } from "react";
import { Button, ListGroup, Form, Row, Col, Modal } from "react-bootstrap";
import axios from "axios";
import "./ProcessingStatus.css";

function ProcessingStatus() {
  const [requests, setRequests] = useState([]);
  const [pickupDate, setPickupDate] = useState("");
  const [rejectReason, setRejectReason] = useState("");
  const [showRejectModal, setShowRejectModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);

  useEffect(() => {
    // Mocking API data for testing
    const mockRequests = [
      {
        id: 1,
        type: "Plastic",
        volume: 5,
        source: "Farmer A",
        date: "2024-12-10",
        status: "pending",
      },
      {
        id: 2,
        type: "Organic",
        volume: 3,
        source: "Farmer B",
        date: "2024-12-12",
        status: "pending",
      },
      {
        id: 3,
        type: "Glass",
        volume: 2,
        source: "Farmer C",
        date: "2024-12-11",
        status: "confirmed",
      },
    ];
    setRequests(mockRequests);
  }, []);

  const handleConfirmRequest = async (requestId) => {
    try {
      // Mocking the confirm action
      setRequests((prevRequests) =>
        prevRequests.map((request) =>
          request.id === requestId
            ? { ...request, status: "confirmed", pickupDate }
            : request
        )
      );
    } catch (error) {
      console.error("Error confirming request:", error);
    }
  };

  const handleRejectRequest = (requestId) => {
    setSelectedRequest(requestId);
    setShowRejectModal(true);
  };

  const handleRejectSubmit = async () => {
    try {
      // Mocking the reject action
      setRequests((prevRequests) =>
        prevRequests.map((request) =>
          request.id === selectedRequest
            ? { ...request, status: "rejected", rejectReason }
            : request
        )
      );
      setShowRejectModal(false);
      setRejectReason("");
    } catch (error) {
      console.error("Error rejecting request:", error);
    }
  };

  const handlePickupDateSubmit = (requestId) => {
    try {
      // Mocking the schedule pickup date action
      setRequests((prevRequests) =>
        prevRequests.map((request) =>
          request.id === requestId
            ? { ...request, status: "confirmed", pickupDate }
            : request
        )
      );
    } catch (error) {
      console.error("Error scheduling pickup:", error);
    }
  };

  return (
    <div className="processing-status-container">
      <h1>Processing Status</h1>
      <p>Track the status of ongoing waste processing.</p>

      <h3>Waste Requests</h3>
      <ListGroup>
        {requests.length === 0 ? (
          <ListGroup.Item>No requests available</ListGroup.Item>
        ) : (
          requests.map((request) => (
            <ListGroup.Item key={request.id}>
              <Row>
                <Col md={6}>
                  <p><strong>Type:</strong> {request.type}</p>
                  <p><strong>Volume:</strong> {request.volume} tons</p>
                  <p><strong>Source:</strong> {request.source}</p>
                  <p><strong>Date:</strong> {request.date}</p>
                </Col>
                <Col md={6}>
                  {request.status === "pending" && (
                    <>
                      <Button
                        variant="success"
                        onClick={() => handleConfirmRequest(request.id)}
                        className="mr-2"
                      >
                        Confirm
                      </Button>
                      <Button
                        variant="danger"
                        onClick={() => handleRejectRequest(request.id)}
                      >
                        Reject
                      </Button>
                    </>
                  )}
                  {request.status === "confirmed" && (
                    <>
                      <p>Pickup Scheduled for: {pickupDate}</p>
                      <Button
                        variant="primary"
                        onClick={() => handlePickupDateSubmit(request.id)}
                        className="mt-2"
                      >
                        Schedule Pickup
                      </Button>
                    </>
                  )}
                  {request.status === "rejected" && (
                    <p><strong>Reason for Rejection:</strong> {request.rejectReason}</p>
                  )}
                </Col>
              </Row>
              {request.status === "pending" && (
                <Row className="mt-3">
                  <Col md={12}>
                    <Form.Control
                      type="date"
                      value={pickupDate}
                      onChange={(e) => setPickupDate(e.target.value)}
                      placeholder="Schedule Pickup Date"
                    />
                  </Col>
                </Row>
              )}
            </ListGroup.Item>
          ))
        )}
      </ListGroup>

      {/* Reject Modal */}
      <Modal show={showRejectModal} onHide={() => setShowRejectModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Reject Reason</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Provide a reason for rejection:</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={rejectReason}
              onChange={(e) => setRejectReason(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowRejectModal(false)}>
            Close
          </Button>
          <Button variant="danger" onClick={handleRejectSubmit}>
            Submit Rejection
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ProcessingStatus;
