import React, { useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Login.css";
import Header from "../common/header/Header";

export default function AdminLogin() {
  const admin = useRef();
  const pass = useRef();
  const [error, setError] = useState("");
  const [isHeaderVisible, setIsHeaderVisible] = useState(true); // Manage Header visibility
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    const adminVal = admin.current.value.trim();
    const passVal = pass.current.value.trim();

    // Validation for empty fields
    if (!adminVal || !passVal) {
      setError("Username and password cannot be empty.");
      return;
    }

    // Check credentials
    if (adminVal === "admin" && passVal === "password") {
      setError("");
      setIsHeaderVisible(false); // Hide the Header
      navigate("/admindash"); // Redirect to admin dashboard
    } else {
      setError("Please enter valid credentials");
    }
  };

  return (
    <div className="unique-login-page">
      {/* Conditionally render Header */}
      {isHeaderVisible && <Header />}
      <div className="unique-login-container">
        <div className="unique-form-container">
          <Link to="/" className="signup-link">Back</Link>
          <h1 className="unique-form-title">Admin</h1>
          <h2 className="unique-form-title">Sign In</h2>
          <form className="unique-form unique-signin-form" onSubmit={handleLogin}>
            <div className="unique-form-group">
              <label htmlFor="username">Username</label>
              <input
                className="unique-form-input"
                type="text"
                name="username"
                placeholder="Enter your username"
                ref={admin}
              />
            </div>
            <div className="unique-form-group">
              <label htmlFor="password">Password</label>
              <input
                className="unique-form-input"
                type="password"
                name="password"
                placeholder="Enter your password"
                ref={pass}
              />
            </div>
            <div className="unique-form-options">
              <label className="unique-checkbox-label">
                <input type="checkbox" id="keepSignedIn" />
                Keep me signed in
              </label>
            </div>
            <button type="submit" className="unique-form-submit-button">
              Sign In
            </button>
          </form>
          {error && <p className="error-message">{error}</p>}
        </div>
      </div>
    </div>
  );
}
