import React, { useState, useEffect } from "react";
import { Form, Button, Col, Row, InputGroup, ListGroup } from "react-bootstrap";
import WasteItem from "./WasteItem";
import axios from "axios";
import "./Intake.css";

function WasteIntake() {
  const [intakes, setIntakes] = useState([]);
  const [typeFilter, setTypeFilter] = useState("");
  const [dateFilter, setDateFilter] = useState("");
  const [newIntake, setNewIntake] = useState({
    type: "",
    volume: "",
    source: "",
    date: "",
    farmerName: "",
    place: "",
    weight: "",
    pricePerTon: "",
    totalPrice: ""
  });

  useEffect(() => {
    fetchIntakes();
  }, []);

  const fetchIntakes = async () => {
    try {
      const response = await axios.get("/api/intakes");
      setIntakes(response.data);
    } catch (error) {
      console.error("Error fetching intakes:", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const updatedIntake = { ...newIntake, [name]: value };

    // Automatically calculate total price if weight and pricePerTon are entered
    if (name === "weight" || name === "pricePerTon") {
      const weight = parseFloat(updatedIntake.weight) || 0;
      const pricePerTon = parseFloat(updatedIntake.pricePerTon) || 0;
      updatedIntake.totalPrice = (weight * pricePerTon).toFixed(2);
    }

    setNewIntake(updatedIntake);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/intakes", newIntake);
      fetchIntakes();
      setNewIntake({
        type: "",
        volume: "",
        source: "",
        date: "",
        farmerName: "",
        place: "",
        weight: "",
        pricePerTon: "",
        totalPrice: ""
      });
    } catch (error) {
      console.error("Error adding new intake:", error);
    }
  };

  const filteredIntakes = intakes.filter((intake) => {
    return (
      (typeFilter ? intake.type.toLowerCase().includes(typeFilter.toLowerCase()) : true) &&
      (dateFilter ? intake.date.includes(dateFilter) : true)
    );
  });

  return (
    <div className="container">
      <div className="waste-container">
        <h1 className="page-title">Waste Intake Management</h1>
        <p className="page-description">Track and manage the incoming waste.</p>

        <Row className="filter-section mb-4">
          <Col md={4}>
            <InputGroup className="input-group-custom">
              <InputGroup.Text>Search by Type</InputGroup.Text>
              <Form.Control
                type="text"
                value={typeFilter}
                onChange={(e) => setTypeFilter(e.target.value)}
                placeholder="Waste type"
              />
            </InputGroup>
          </Col>
          <Col md={4}>
            <InputGroup className="input-group-custom">
              <InputGroup.Text>Search by Date</InputGroup.Text>
              <Form.Control
                type="date"
                value={dateFilter}
                onChange={(e) => setDateFilter(e.target.value)}
              />
            </InputGroup>
          </Col>
        </Row>

        <div className="form-section">
          <h3>Add New Intake</h3>
          <Form onSubmit={handleFormSubmit}>
            <Row>
              <Col md={3}>
                <Form.Control
                  type="text"
                  name="type"
                  value={newIntake.type}
                  onChange={handleInputChange}
                  placeholder="Waste Type"
                  required
                />
              </Col>
              <Col md={3}>
                <Form.Control
                  type="number"
                  name="volume"
                  value={newIntake.volume}
                  onChange={handleInputChange}
                  placeholder="Volume (tons)"
                  required
                />
              </Col>
              <Col md={3}>
                <Form.Control
                  type="text"
                  name="source"
                  value={newIntake.source}
                  onChange={handleInputChange}
                  placeholder="Source"
                  required
                />
              </Col>
              <Col md={3}>
                <Form.Control
                  type="date"
                  name="date"
                  value={newIntake.date}
                  onChange={handleInputChange}
                  required
                />
              </Col>
            </Row>
            <Row className="mt-3">
              <Col md={3}>
                <Form.Control
                  type="text"
                  name="farmerName"
                  value={newIntake.farmerName}
                  onChange={handleInputChange}
                  placeholder="Farmer Name"
                  required
                />
              </Col>
              <Col md={3}>
                <Form.Control
                  type="text"
                  name="place"
                  value={newIntake.place}
                  onChange={handleInputChange}
                  placeholder="Place"
                  required
                />
              </Col>
              <Col md={3}>
                <Form.Control
                  type="number"
                  name="weight"
                  value={newIntake.weight}
                  onChange={handleInputChange}
                  placeholder="Weight (tons)"
                  required
                />
              </Col>
              <Col md={3}>
                <Form.Control
                  type="number"
                  name="pricePerTon"
                  value={newIntake.pricePerTon}
                  onChange={handleInputChange}
                  placeholder="Price per Ton"
                  required
                />
              </Col>
            </Row>
            <Row className="mt-3">
              <Col md={3}>
                <Form.Control
                  type="text"
                  name="totalPrice"
                  value={newIntake.totalPrice}
                  readOnly
                  placeholder="Total Price"
                />
              </Col>
            </Row>
            <Button className="submit-button mt-3" type="submit">
              Add Intake
            </Button>
          </Form>
        </div>

        <div className="waste-list">
          <h3 className="mt-4">Waste Intake List</h3>
          <ListGroup>
            {filteredIntakes.length === 0 ? (
              <ListGroup.Item className="no-records">
                No waste intake records found
              </ListGroup.Item>
            ) : (
              filteredIntakes.map((intake) => <WasteItem key={intake.id} intake={intake} />)
            )}
          </ListGroup>
        </div>
      </div>
    </div>
  );
}

export default WasteIntake;
