import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ProductListing.css";

const ProductListing = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProductsFromWasteIntake = async () => {
      try {
        const response = await axios.get("/api/waste-products");
        setProducts(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch products derived from waste intake. Please try again.");
        setLoading(false);
        console.error(err);
      }
    };

    fetchProductsFromWasteIntake();
  }, []);

  return (
    <div className="container">
        <div className="row">
    <div className="product-listings-container">
      <h1>Products Derived from Waste Intake</h1>

      {loading && <p>Loading products...</p>}
      {error && <p className="error-message">{error}</p>}

      <div className="product-list">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <h3>{product.name}</h3>
              <p>Type: {product.type}</p>
              <p>Quantity: {product.quantity}</p>
              <p>Price: ${product.price}</p>
              <p>Derived From Waste: {product.wasteSource}</p>
            </div>
          ))
        ) : (
          !loading && <p>No products derived from waste intake are available.</p>
        )}
      </div>
    </div>
    </div>
    </div>
  );
};

export default ProductListing;
