import React, { useState, useEffect } from "react";
import { Form, Button, Col, Row, InputGroup, ListGroup } from "react-bootstrap";
import axios from "axios";
import "./Intake.css";

function WasteIntake() {
  const [intakes, setIntakes] = useState([]);
  const [filters, setFilters] = useState({ name: "", date: "" });
  const [newIntake, setNewIntake] = useState({
    type: "",
    farmerName: "",
    place: "",
    weight: "",
    pricePerTon: "",
    totalPrice: "",
    date: ""
  });

  useEffect(() => {
    fetchIntakes();
  }, []);

  const fetchIntakes = async () => {
    try {
      const response = await axios.get("/api/intakes");
      setIntakes(response.data);
    } catch (error) {
      console.error("Error fetching intakes:", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewIntake((prev) => {
      const updated = { ...prev, [name]: value };
      if (name === "weight" || name === "pricePerTon") {
        const weight = parseFloat(updated.weight) || 0;
        const pricePerTon = parseFloat(updated.pricePerTon) || 0;
        updated.totalPrice = (weight * pricePerTon).toFixed(2);
      }
      return updated;
    });
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/intakes", newIntake);
      fetchIntakes();
      setNewIntake({ type: "", farmerName: "", place: "", weight: "", pricePerTon: "", totalPrice: "", date: "" });
    } catch (error) {
      console.error("Error adding new intake:", error);
    }
  };

  const filteredIntakes = intakes.filter((intake) => {
    return (
      (filters.name ? intake.farmerName.toLowerCase().includes(filters.name.toLowerCase()) : true) &&
      (filters.date ? intake.date.includes(filters.date) : true)
    );
  });

  return (
    <div className="container">
      <div className="waste-container">
        <h1 className="page-title">Waste Intake Management</h1>
        <p className="page-description">Track and manage the incoming waste efficiently.</p>

        <Row className="filter-section mb-4">
          <Col md={4}>
            <InputGroup className="input-group-custom">
              <InputGroup.Text>Search by Farmer Name</InputGroup.Text>
              <Form.Control
                type="text"
                name="name"
                value={filters.name}
                onChange={handleFilterChange}
                placeholder="Farmer name"
              />
            </InputGroup>
          </Col>
          <Col md={4}>
            <InputGroup className="input-group-custom">
              <InputGroup.Text>Search by Date</InputGroup.Text>
              <Form.Control
                type="date"
                name="date"
                value={filters.date}
                onChange={handleFilterChange}
              />
            </InputGroup>
          </Col>
        </Row>

        <div className="form-section">
          <h3>Add New Intake</h3>
          <Form onSubmit={handleFormSubmit}>
            <Row>
              <Col md={2}>
                <Form.Control
                  type="text"
                  name="type"
                  value={newIntake.type}
                  onChange={handleInputChange}
                  placeholder="Waste Type"
                  required
                />
              </Col>
              <Col md={2}>
                <Form.Control
                  type="text"
                  name="farmerName"
                  value={newIntake.farmerName}
                  onChange={handleInputChange}
                  placeholder="Farmer Name"
                  required
                />
              </Col>
              <Col md={2}>
                <Form.Control
                  type="text"
                  name="place"
                  value={newIntake.place}
                  onChange={handleInputChange}
                  placeholder="Place"
                  required
                />
              </Col>
              <Col md={2}>
                <Form.Control
                  type="number"
                  name="weight"
                  value={newIntake.weight}
                  onChange={handleInputChange}
                  placeholder="Weight (tons)"
                  required
                />
              </Col>
              <Col md={2}>
                <Form.Control
                  type="number"
                  name="pricePerTon"
                  value={newIntake.pricePerTon}
                  onChange={handleInputChange}
                  placeholder="Price per Ton"
                  required
                />
              </Col>
              <Col md={2}>
                <Form.Control
                  type="date"
                  name="date"
                  value={newIntake.date}
                  onChange={handleInputChange}
                  required
                />
              </Col>
            </Row>
            <Row className="mt-3">
              <Col md={3}>
                <Form.Control
                  type="text"
                  value={newIntake.totalPrice}
                  readOnly
                  placeholder="Total Price"
                />
              </Col>
            </Row>
            <Button className="submit-button mt-3" type="submit">
              Add Intake
            </Button>
          </Form>
        </div>

        <div className="waste-list mt-4">
          <h3>Filtered Waste Intakes</h3>
          {filteredIntakes.length === 0 ? (
            <p>No waste intake records found</p>
          ) : (
            <ListGroup>
              {filteredIntakes.map((intake) => (
                <ListGroup.Item key={intake.id}>
                  <strong>Type:</strong> {intake.type}, <strong>Farmer:</strong> {intake.farmerName}, <strong>Place:</strong> {intake.place}, <strong>Weight:</strong> {intake.weight} tons, <strong>Total Price:</strong> â‚¹{intake.totalPrice}, <strong>Date:</strong> {intake.date}
                </ListGroup.Item>
              ))}
            </ListGroup>
          )}
        </div>
      </div>
    </div>
  );
}

export default WasteIntake;
