import React, { useState } from "react";
import { Form, Button, Col, Row, Container } from "react-bootstrap";
import axios from "axios";
import "./Intake.css";

function AddStock() {
  const [newStock, setNewStock] = useState({
    wasteId: "",
    wasteType: "",
    weight: "",
  });

  const [deleteId, setDeleteId] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStock({
      ...newStock,
      [name]: value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8085/stock/add", newStock);
      alert("Stock added successfully!");
      setNewStock({ wasteId: "", wasteType: "", weight: "" });
    } catch (error) {
      console.error("Error adding stock:", error);
    }
  };

  const handleDeleteStock = async (e) => {
    e.preventDefault();
    if (!deleteId) {
      alert("Please provide a valid stock ID.");
      return;
    }
    try {
      await axios.delete(`http://localhost:8085/stock/delete/${deleteId}`);
      alert(`Stock with ID ${deleteId} deleted successfully!`);
      setDeleteId("");
    } catch (error) {
      console.error("Error deleting stock:", error);
      alert("Error deleting stock. Please check the ID.");
    }
  };

  return (
    <Container className="stock-container mt-5">
      <h1 className="page-title text-center">Stock Management</h1>
      <p className="page-description text-center">
        Add and manage stock records efficiently.
      </p>

      {/* Form for Adding Stock */}
      <div className="form-section mt-4">
        <h3 className="text-center">Add New Stock</h3>
        <Form onSubmit={handleFormSubmit}>
          <Row className="mb-3">
            <Col md={4}>
              <Form.Control
                type="text"
                name="wasteId"
                value={newStock.wasteId}
                onChange={handleInputChange}
                placeholder="Waste ID"
                required
              />
            </Col>
            <Col md={4}>
              <Form.Control
                type="text"
                name="wasteType"
                value={newStock.wasteType}
                onChange={handleInputChange}
                placeholder="Waste Type"
                required
              />
            </Col>
            <Col md={4}>
              <Form.Control
                type="number"
                name="weight"
                value={newStock.weight}
                onChange={handleInputChange}
                placeholder="Weight (tons)"
                required
              />
            </Col>
          </Row>

          <Button className="submit-button w-100 mt-3" type="submit">
            Add Stock
          </Button>
        </Form>
      </div>

      {/* Form for Deleting Stock */}
      <div className="form-section mt-5">
        <h3 className="text-center">Delete Stock</h3>
        <Form onSubmit={handleDeleteStock}>
          <Row className="mb-3">
            <Col md={8}>
              <Form.Control
                type="text"
                value={deleteId}
                onChange={(e) => setDeleteId(e.target.value)}
                placeholder="Enter Stock ID to Delete"
                required
              />
            </Col>
            <Col md={4}>
              <Button className="delete-button w-100" type="submit">
                Delete Stock
              </Button>
            </Col>
          </Row>
        </Form>
      </div>
    </Container>
  );
}

export default AddStock;
