import React, { useState, useEffect } from "react";
import { Button, Row, Col, Modal, Card, Form } from "react-bootstrap";

function ProcessingStatus() {
  const [requests, setRequests] = useState([]);
  const [pickupDate, setPickupDate] = useState("");
  const [additionalInfo, setAdditionalInfo] = useState("");
  const [showScheduleModal, setShowScheduleModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);

  // Fetch data from the API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:8085/waste-details/orders");
        const data = await response.json();
        setRequests(data); // Assuming the response is an array
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, []);

  // Handle Confirm Button Click (Status = ACCEPTED)
  const handleConfirmRequest = async (requestId) => {
    try {
      // Update status to ACCEPTED in the database
      const response = await fetch(`http://localhost:8085/waste-details/update/${requestId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          status: "ACCEPTED", // Set status to ACCEPTED
          message: "", // Initially, thereâ€™s no message
        }),
      });

      if (response.ok) {
        // Update status locally
        setRequests((prevRequests) =>
          prevRequests.map((request) =>
            request.id === requestId ? { ...request, status: "ACCEPTED" } : request
          )
        );
        setSelectedRequest(requestId); // Set the selected request to schedule
        setShowScheduleModal(true); // Open modal to schedule pickup
      } else {
        console.error("Failed to update status");
      }
    } catch (error) {
      console.error("Error updating status:", error);
    }
  };

  // Handle Schedule Pickup Button Click (Update Status = PICKUP_SCHEDULED)
  const handleScheduleSubmit = async () => {
    if (!pickupDate || !additionalInfo) {
      alert("Please fill in the pickup date and additional info.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8085/waste-details/update/${selectedRequest}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          status: "PICKUP_SCHEDULED", // Change status to PICKUP_SCHEDULED
          pickupDate: pickupDate,
          message: additionalInfo, // Use the entered additional info
        }),
      });

      if (response.ok) {
        // Update the status and additional info locally
        setRequests((prevRequests) =>
          prevRequests.map((request) =>
            request.id === selectedRequest
              ? {
                  ...request,
                  status: "PICKUP_SCHEDULED",
                  pickupDate: pickupDate,
                  message: additionalInfo,
                }
              : request
          )
        );
        setShowScheduleModal(false); // Close modal
        setPickupDate(""); // Reset pickup date
        setAdditionalInfo(""); // Reset additional info
      } else {
        console.error("Failed to schedule pickup");
      }
    } catch (error) {
      console.error("Error scheduling pickup:", error);
    }
  };

  return (
    <div className="container">
      <div className="processing-status-container">
        <h1>Processing Status</h1>
        <p>Track the status of ongoing waste processing.</p>

        <h3>Waste Requests</h3>
        <div className="requests-list">
          {requests.length === 0 ? (
            <p>No requests available</p>
          ) : (
            requests.map((request) => (
              <Card key={request.id} className="request-card mb-3">
                <Card.Img
                  variant="top"
                  src={request.imagePath}
                  alt={`${request.wasteType} image`}
                  className="card-image p-2 rounded-4"
                />
                <Card.Body>
                  <Row>
                    <Col md={6}>
                      <p><strong>Name:</strong> {request.name}</p>
                      <p><strong>Waste Type:</strong> {request.wasteType}</p>
                      <p><strong>Address:</strong> {request.address}</p>
                      <p><strong>Date Created:</strong> {new Date(request.createdAt).toLocaleDateString()}</p>
                    </Col>
                    <Col md={6}>
                      {request.status === null && (
                        <>
                          <Button
                            variant="success"
                            onClick={() => handleConfirmRequest(request.id)}
                            className="mr-2"
                          >
                            Confirm
                          </Button>
                          <Button
                            variant="danger"
                            // Implement reject functionality here if needed
                          >
                            Reject
                          </Button>
                        </>
                      )}
                      {request.status === "ACCEPTED" && (
                        <>
                          <p>Pickup Scheduled for: {request.pickupDate}</p>
                          <Button
                            variant="primary"
                            onClick={() => setShowScheduleModal(true)}
                            className="mt-2"
                          >
                            Schedule Pickup
                          </Button>
                        </>
                      )}
                      {request.status === "PICKUP_SCHEDULED" && (
                        <p>
                          Pickup scheduled for: {request.pickupDate}<br />
                          <strong>Additional Info:</strong> {request.message}
                        </p>
                      )}
                    </Col>
                  </Row>
                </Card.Body>
              </Card>
            ))
          )}
        </div>
      </div>

      {/* Schedule Pickup Modal */}
      <Modal show={showScheduleModal} onHide={() => setShowScheduleModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Schedule Pickup</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Pickup Date:</Form.Label>
            <Form.Control
              type="date"
              value={pickupDate}
              onChange={(e) => setPickupDate(e.target.value)}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Additional Information:</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={additionalInfo}
              onChange={(e) => setAdditionalInfo(e.target.value)}
              placeholder="Enter additional info"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowScheduleModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleScheduleSubmit}>
            Schedule Pickup
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ProcessingStatus;
