import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import "./Dashboard.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const EmployeeDashboard = () => {
  const [data, setData] = useState({
    tasksCompleted: 120,
    ongoingProjects: 5,
    hoursLoggedThisMonth: 160,
    upcomingDeadlines: [
      { project: "Client Website Redesign", deadline: "2025-02-20" },
      { project: "Mobile App Development", deadline: "2025-03-05" },
    ],
    productivityData: [
      { date: "2025-02-01", hours: 8 },
      { date: "2025-02-02", hours: 7 },
      { date: "2025-02-03", hours: 8 },
      { date: "2025-02-04", hours: 9 },
      { date: "2025-02-05", hours: 6 },
    ],
  });

  useEffect(() => {
    const fetchData = () => {
      setData(prevData => ({ ...prevData }));
    };
    fetchData();
  }, []);

  const lineChartData = {
    labels: data.productivityData.map(item => item.date),
    datasets: [
      {
        label: "Productivity (Hours Logged)",
        data: data.productivityData.map(item => item.hours),
        borderColor: "#007BFF",
        backgroundColor: "rgba(0, 123, 255, 0.2)",
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">Employee Dashboard</h1>

      <div className="dashboard-metrics">
        <div className="metric-card">
          <h2>Tasks Completed</h2>
          <p>{data.tasksCompleted}</p>
        </div>
        <div className="metric-card">
          <h2>Ongoing Projects</h2>
          <p>{data.ongoingProjects}</p>
        </div>
        <div className="metric-card">
          <h2>Hours Logged This Month</h2>
          <p>{data.hoursLoggedThisMonth} hrs</p>
        </div>
      </div>

      <div className="charts-section">
        <h2>Productivity Over Time</h2>
        <Line data={lineChartData} />
      </div>

      <div className="deadlines-section">
        <h2>Upcoming Deadlines</h2>
        <ul>
          {data.upcomingDeadlines.map((deadline, index) => (
            <li key={index}>
              {deadline.project}: {deadline.deadline}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
