import React, { useState, useEffect } from "react";
import { Line, Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend } from "chart.js";
import { Card, CardContent } from "@/components/ui/card";
import { AlertCircle, CheckCircle, XCircle } from "lucide-react";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const EmployeeDashboard = () => {
  const [dashboardData, setDashboardData] = useState({
    totalWasteCollected: 800,
    totalWasteProcessed: 700,
    productsGenerated: {
      compost: 400,
      biofuel: 300,
    },
    ongoingRequests: 12,
    environmentalImpact: 120, // in tons of CO2 saved
    wasteCollectionData: [
      { date: "2024-01-01", volume: 50 },
      { date: "2024-01-02", volume: 80 },
      { date: "2024-01-03", volume: 90 },
      { date: "2024-01-04", volume: 60 },
    ],
    productGenerationData: [
      { type: "Compost", quantity: 400 },
      { type: "Biofuel", quantity: 300 },
    ],
    taskUpdates: [
      { task: "Pickup from Green Valley Farms", status: "Scheduled" },
      { task: "Composting at Eco Biogas Plant", status: "In Progress" },
      { task: "Report Submission for January", status: "Completed" },
    ],
  });

  useEffect(() => {
    // Simulate fetching data from an API
    const fetchData = () => {
      setDashboardData(prevData => ({ ...prevData }));
    };
    fetchData();
  }, []);

  const wasteCollectionChartData = {
    labels: dashboardData.wasteCollectionData.map(item => item.date),
    datasets: [
      {
        label: "Waste Collection Over Time (tons)",
        data: dashboardData.wasteCollectionData.map(item => item.volume),
        borderColor: "#4CAF50",
        backgroundColor: "rgba(76, 175, 80, 0.2)",
        tension: 0.4,
      },
    ],
  };

  const productGenerationChartData = {
    labels: dashboardData.productGenerationData.map(item => item.type),
    datasets: [
      {
        label: "Products Generated (tons)",
        data: dashboardData.productGenerationData.map(item => item.quantity),
        backgroundColor: ["#FF9800", "#03A9F4"],
      },
    ],
  };

  return (
    <div className="p-4 grid gap-4 grid-cols-1 md:grid-cols-2">
      {/* Metrics Cards */}
      <Card>
        <CardContent>
          <h2 className="text-xl font-semibold">Total Waste Collected</h2>
          <p className="text-2xl text-green-600">{dashboardData.totalWasteCollected} tons</p>
        </CardContent>
      </Card>
      <Card>
        <CardContent>
          <h2 className="text-xl font-semibold">Total Waste Processed</h2>
          <p className="text-2xl text-blue-600">{dashboardData.totalWasteProcessed} tons</p>
        </CardContent>
      </Card>
      <Card>
        <CardContent>
          <h2 className="text-xl font-semibold">Ongoing Service Requests</h2>
          <p className="text-2xl text-orange-600">{dashboardData.ongoingRequests}</p>
        </CardContent>
      </Card>
      <Card>
        <CardContent>
          <h2 className="text-xl font-semibold">Environmental Impact</h2>
          <p className="text-2xl text-purple-600">{dashboardData.environmentalImpact} tons COâ‚‚ saved</p>
        </CardContent>
      </Card>

      {/* Charts Section */}
      <div className="col-span-2">
        <h2 className="text-2xl font-bold mb-2">Waste Collection Over Time</h2>
        <Line data={wasteCollectionChartData} />
      </div>
      <div className="col-span-2">
        <h2 className="text-2xl font-bold mb-2">Product Generation Breakdown</h2>
        <Bar data={productGenerationChartData} />
      </div>

      {/* Task Updates */}
      <div className="col-span-2">
        <h2 className="text-2xl font-bold mb-2">Task Updates</h2>
        <ul>
          {dashboardData.taskUpdates.map((task, index) => (
            <li key={index} className="flex items-center gap-2 mb-2">
              {task.status === "Completed" && <CheckCircle className="text-green-500" />}
              {task.status === "In Progress" && <AlertCircle className="text-yellow-500" />}
              {task.status === "Scheduled" && <XCircle className="text-red-500" />}
              <span>{task.task} - <strong>{task.status}</strong></span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
