import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import './History.css';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    async function fetchData() {
      try {
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const fetchedOrders = intakeResponse.data;

        const wasteResponse = await axios.get(`http://localhost:8085/waste-details/orders/${userId}`);
        const wasteOrders = wasteResponse.data.filter(order => order.status === "COLLECTED");

        const wasteImagesMap = {};
        wasteOrders.forEach(order => {
          wasteImagesMap[order.id] = order.imagePath;
        });

        const matchedOrders = fetchedOrders.filter(order => wasteImagesMap[order.wasteId]);
        setOrders(matchedOrders);
        setImages(wasteImagesMap);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    if (userId) {
      fetchData();
    }
  }, [userId]);

  const generateInvoice = (order) => {
    const doc = new jsPDF();

    // Header
    doc.setFontSize(18);
    doc.text('FarmEco - Waste Selling Company', 105, 20, { align: 'center' });
    doc.setFontSize(12);
    doc.text('Eco-Friendly Solutions for Agricultural Waste Management', 105, 28, { align: 'center' });
    doc.line(10, 40, 200, 40); // Divider

    // Invoice Info
    doc.setFontSize(14);
    doc.text('Invoice', 10, 50);
    doc.setFontSize(12);
    doc.text(`Invoice Number: ${order.id}`, 10, 60);
    doc.text(`Order Date: ${new Date(order.collectedDate).toLocaleDateString()}`, 10, 70);

    // Farmer Info
    doc.setFontSize(14);
    doc.text('Customer Details', 10, 80);

    const customerDetails = [
      ['Farmer Name:', order.farmerName],
      ['Place:', order.place],
      ['Collected By:', order.collectedBy]
    ];

    doc.autoTable({
      startY: 85,
      head: [['Field', 'Value']],
      body: customerDetails,
      theme: 'striped',
      columnStyles: { 0: { fontStyle: 'bold' } }
    });

    // Order Summary
    doc.setFontSize(14);
    doc.text('Order Summary', 10, doc.lastAutoTable.finalY + 10);

    const orderSummary = [
      ['Item Description', 'Weight (kg)', 'Rate (₹/kg)', 'Total Price (₹)'],
      ['Agricultural Waste', `${order.weight}`, `${order.rate}`, `${order.totalPrice}`]
    ];

    doc.autoTable({
      startY: doc.lastAutoTable.finalY + 15,
      head: [orderSummary[0]],
      body: [orderSummary[1]],
      theme: 'grid'
    });

    // Footer
    doc.setFontSize(10);
    doc.text('Thank you for partnering with FarmEco!', 105, doc.lastAutoTable.finalY + 20, { align: 'center' });
    doc.text('For support, contact us at support@farmeco.com or +91-1234567890', 105, doc.lastAutoTable.finalY + 25, { align: 'center' });

    // Save the PDF
    doc.save(`Invoice_Order_${order.id}.pdf`);
  };

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      <div className="card-container">
        {orders.map(order => (
          <div className="order-card" key={order.id}>
            <div className="d-flex justify-content-center align-items-center">
              <img
                src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                alt="Order Visual"
                height="150px"
                width="200px"
              />
            </div>
            <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
            <p><strong>Place:</strong> {order.place}</p>
            <p><strong>Weight:</strong> {order.weight} kg</p>
            <p><strong>Collected By:</strong> {order.collectedBy}</p>
            <p><strong>Farmer Name:</strong> {order.farmerName}</p>
            <p><strong>Rate:</strong> ₹{order.rate}</p>
            <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
            <button
              type="button"
              className="btn btn-danger"
              onClick={() => generateInvoice(order)}
            >
              Generate Invoice
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default History;
