import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    setErrorMessage("");

    if (!username.trim() || !password.trim() || !role) {
      setErrorMessage("All fields are required!");
      return;
    }

    if (!/^[a-zA-Z0-9_.-]+$/.test(username)) {
      setErrorMessage("Invalid username. Only alphanumeric characters and _ . - are allowed.");
      return;
    }

    if (password.length < 6) {
      setErrorMessage("Password must be at least 6 characters long.");
      return;
    }

    if (role === "Admin") {
      navigate("/admin-dashboard");
    } else if (role === "Employee") {
      navigate("/employee-dashboard");
    } else if (role === "Customer") {
      navigate("/customer-dashboard");
    }
  };

  return (
    <div className="unique-login-page">
      <div className="unique-login-container">
        <div className="unique-form-container">
          <h2 className="unique-form-title">Login</h2>
          {errorMessage && <p className="unique-error-message">{errorMessage}</p>}
          <form className="unique-form">
            <div className="unique-form-group">
              <label htmlFor="username">Username</label>
              <input
                className="unique-form-input"
                type="text"
                placeholder="Enter your username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="unique-form-group">
              <label htmlFor="password">Password</label>
              <input
                className="unique-form-input"
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="unique-form-group">
              <label htmlFor="role">Select Role</label>
              <select
                className="unique-form-input"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              >
                <option value="">--Select Role--</option>
                <option value="Admin">Admin</option>
                <option value="Employee">Employee</option>
                <option value="Customer">Customer</option>
              </select>
            </div>
            <button type="button" className="unique-form-submit-button" onClick={handleLogin}>
              Login
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
