import React, { useEffect, useState } from 'react';
import './History.css';
import axios from 'axios';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    async function fetchData() {
      try {
        // Fetch intake data first
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const fetchedOrders = intakeResponse.data;

        // Fetch waste details for the specific userId
        const wasteResponse = await axios.get(`http://localhost:8085/waste-details/orders/${userId}`);
        const wasteOrders = wasteResponse.data.filter(order => order.status === "COLLECTED");

        const wasteImagesMap = {};
        wasteOrders.forEach(order => {
          wasteImagesMap[order.id] = order.imagePath;
        });

        // Map images to the intake orders if wasteId matches
        const matchedOrders = fetchedOrders.filter(order => wasteImagesMap[order.wasteId]);

        // Set matched orders and images
        setOrders(matchedOrders);
        setImages(wasteImagesMap);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    if (userId) {
      fetchData();
    }
  }, [userId]);

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      <div className="card-container">
        {orders.map(order => (
          <div className="order-card" key={order.id}>
            <div className="d-flex justify-content-center align-items-center">
              <img
                src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                alt="Order Visual"
                height="150px"
                width="200px"
              />
            </div>
            <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
            <p><strong>Place:</strong> {order.place}</p>
            <p><strong>Weight:</strong> {order.weight} kg</p>
            <p><strong>Collected By:</strong> {order.collectedBy}</p>
            <p><strong>Farmer Name:</strong> {order.farmerName}</p>
            <p><strong>Rate:</strong> ₹{order.rate}</p>
            <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
            <button type="button" className="btn btn-danger">Invoice</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default History;
