import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import { Link } from 'react-router-dom';
import './History.css';

function FarmerDashboard() {
  const [farmer, setFarmer] = useState(null);
  const [completedOrders, setCompletedOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    async function fetchData() {
      try {
        if (!userId) {
          console.error("No user ID found in localStorage");
          setLoading(false);
          return;
        }

        // Fetch Farmer Details
        const farmerResponse = await axios.get(`http://localhost:8085/farmer/${userId}`);
        setFarmer(farmerResponse.data);

        // Fetch Orders from Intake Table (For Completed)
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const allOrders = intakeResponse.data;

        // Fetch Waste Orders (For Collected & Rejected)
        const wasteResponse = await axios.get(`http://localhost:8085/waste-details/orders/${userId}`);
        const wasteOrders = wasteResponse.data;

        // Mapping Image Paths
        const wasteImagesMap = {};
        wasteOrders.forEach(order => {
          wasteImagesMap[order.id] = order.imagePath;
        });

        // Completed Orders (Status: COLLECTED)
        const matchedCompletedOrders = allOrders.filter(order => wasteImagesMap[order.wasteId]);
        setCompletedOrders(matchedCompletedOrders);

        // Filter Orders for Collected & Rejected
        const filtered = wasteOrders.filter(order => ["COLLECTED", "REJECTED"].includes(order.status));
        setFilteredOrders(filtered);

        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    }

    fetchData();
  }, [userId]);

  const handleLogout = () => {
    localStorage.removeItem("userId");
    alert("Logged out successfully!");
    window.location.href = "/";
  };

  const openTrackOrder = (order) => {
    setSelectedOrder(order);
  };

  const generateInvoice = (order) => {
    const doc = new jsPDF();

    // Header Section
    doc.setFontSize(22);
    doc.text('FarmEco Invoice', 105, 20, { align: 'center' });

    doc.setFontSize(12);
    doc.text('Waste Selling Company', 105, 28, { align: 'center' });
    doc.line(10, 35, 200, 35);

    // Company Info Section
    doc.setFontSize(10);
    doc.text('FarmEco Pvt Ltd.', 10, 45);
    doc.text('123 Greenfield Avenue, Pune, Maharashtra', 10, 50);
    doc.text('Contact: +91-1234567890 | Email: support@farmeco.com', 10, 55);

    // Invoice Info
    const invoiceNumber = `FEC-${order.id.toString().padStart(6, '0')}-${order.wasteId}`;
    doc.setFontSize(12);
    doc.text(`Invoice Number: ${invoiceNumber}`, 140, 45);
    doc.text(`Date: ${new Date(order.collectedDate).toLocaleDateString()}`, 140, 50);

    // Customer Details
    const customerDetails = [
      ['Farmer Name:', order.farmerName],
      ['Place:', order.place],
      ['Collected By:', order.collectedBy]
    ];

    doc.autoTable({
      startY: 70,
      head: [['Field', 'Value']],
      body: customerDetails,
      theme: 'striped',
      columnStyles: { 0: { fontStyle: 'bold', halign: 'left' } },
    });

    // Order Summary
    const orderSummary = [
      ['Item Description', 'Weight (tons)', 'Rate', 'Total Price'],
      ['Agricultural Waste', `${order.weight}`, `${order.rate}`, `${order.totalPrice}`]
    ];

    doc.autoTable({
      startY: doc.lastAutoTable.finalY + 15,
      head: [orderSummary[0]],
      body: [orderSummary[1]],
      theme: 'grid',
    });

    // Footer
    doc.text('Thank you for partnering with FarmEco!', 105, doc.lastAutoTable.finalY + 20, { align: 'center' });
    doc.text('For support, contact us at support@farmeco.com or +91-1234567890', 105, doc.lastAutoTable.finalY + 25, { align: 'center' });

    doc.save(`Invoice_${invoiceNumber}.pdf`);
  };

  if (loading) return <div>Loading...</div>;
  if (!farmer) return <div>Unable to load farmer data. Please try again later.</div>;

  return (
    <div className="farmer-dashboard">
      <header className="dashboard-header">
        <div className="logo">Farm Dashboard</div>
        <div className="profile">
          <span className="p-3">{farmer.name}</span>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <section className="main-dashboard">
        <h2>Welcome, {farmer.name}!</h2>
        <div className="farmer-info">
          <p><strong>Id:</strong> F19050{farmer.id}</p>
          <p><strong>Name:</strong> {farmer.name}</p>
          <p><strong>Address:</strong> {farmer.address}</p>
          <p><strong>Email:</strong> {farmer.email}</p>
        </div>
        <Link to="/custheader/editprofile">
          <button type="button" className="btn btn-secondary">Edit Profile</button>
        </Link>
      </section>

      {/* Completed Orders Section */}
      <section className="order-history">
        <h3>Completed Orders</h3>
        <div className="card-container">
          {completedOrders.map(order => (
            <div className="order-card" key={order.id}>
              <img src={order.imagePath || "https://via.placeholder.com/200x150"} alt="Order" width="200px" height="150px" />
              <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
              <p><strong>Place:</strong> {order.place}</p>
              <p><strong>Weight:</strong> {order.weight} kg</p>
              <p><strong>Collected By:</strong> {order.collectedBy}</p>
              <button className="btn btn-danger" onClick={() => generateInvoice(order)}>Generate Invoice</button>
            </div>
          ))}
        </div>
      </section>

      {/* Collected & Rejected Orders Section */}
      <section className="order-status">
        <h3>Collected & Rejected Orders</h3>
        <div className="card-container">
          {filteredOrders.map(order => (
            <div key={order.id} className={`card ${order.status.toLowerCase()}`}>
              <h4>Order ID: ORD-{order.id}</h4>
              <img src={order.imagePath || "https://via.placeholder.com/200x150"} height="150px" width="200px" alt="Order" />
              <p>Waste Type: {order.wasteType || "Not specified"}</p>
              <p>Status: <span className="order-status-text">{order.status}</span></p>
              <button className="track-button" onClick={() => openTrackOrder(order)}>Track Order</button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default FarmerDashboard;
