import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Dashboard.css";
import i1 from "./assets/Image1.jpg";

const TrackOrder = ({ order }) => {
  if (order.status === "Cancelled") {
    return <p>This order was cancelled. No tracking information available.</p>;
  }

  return (
    <div className="tracking-info">
      <p>
        Track Order:{" "}
        <a href={order.trackingLink} target="_blank" rel="noopener noreferrer">
          {order.trackingLink}
        </a>
      </p>
      {order.currentLocation ? (
        <p>Current Location: {order.currentLocation}</p>
      ) : (
        <p>Tracking information is not available yet.</p>
      )}
    </div>
  );
};

const FarmerDashboard = () => {
  const [farmer, setFarmer] = useState(null);
  const [orderStatus, setOrderStatus] = useState([]); 
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    const userId = localStorage.getItem("userId");

    if (userId) {
      // Fetch farmer profile
      fetch(`http://localhost:8085/api/farmers/get/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          setFarmer(data);
        })
        .catch((error) => {
          console.error("Error fetching farmer data:", error);
        });

      // Fetch farmer orders from the backend API
      fetch(`http://localhost:8085/waste-details/orders/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          setOrderStatus(data);
        })
        .catch((error) => {
          console.error("Error fetching order data:", error);
        });
    } else {
      console.error("No user ID found in localStorage");
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userId");
    alert("Logged out successfully!");
    window.location.href = "/";
  };

  const openTrackOrder = (order) => {
    setSelectedOrder(order);
  };

  if (!farmer) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div className="farmer-dashboard">
        <header className="dashboard-header">
          <div className="logo">Farm Dashboard</div>
          <div className="profile">
            <span className="p-3">{farmer.name}</span>
            <button className="logout-button" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </header>

        <section className="main-dashboard">
          <div className="row">
            <div className="col-xl-10">
              <h2>Welcome, {farmer.name}!</h2>
            </div>
            <div className="col-xl-2 align-content-right">
              <Link to="/custheader/editprofile">
                <button type="button" className="btn btn-secondary">
                  Edit Profile
                </button>
              </Link>
            </div>
            <div className="farmer-info">
              <p>
                <strong>Id:</strong>F19050{farmer.id}
              </p>
              <p>
                <strong>Name:</strong> {farmer.name}
              </p>
              <p>
                <strong>Address:</strong> {farmer.address}
              </p>
              <p>
                <strong>Email:</strong> {farmer.email}
              </p>
            </div>
          </div>
        </section>

        {/* Order Section */}
        <section className="order-status">
          <h3>Your Orders</h3>
          <div className="card-container">
            {orderStatus.length > 0 ? (
              orderStatus.map((order, index) => (
                <div key={index} className={`card ${order.status.toLowerCase()}`}>
                  <h4>Order ID: {order.id}</h4>
                  <div className="d-flex justify-content-center align-items-center my-2">
                    <img src={i1} height="150px" width="200px" alt="Product" />
                  </div>
                  <p>
                    Status: <span className="order-status-text">{order.status}</span>
                  </p>
                  {order.pickupDate && <p>Pickup: {order.pickupDate}</p>}
                  <p>Driver Name: {order.driverName || "Not Assigned"}</p>
                  <button
                    className="track-button"
                    onClick={() => openTrackOrder(order)}
                  >
                    Track Order
                  </button>
                </div>
              ))
            ) : (
              <p>No orders available.</p>
            )}
          </div>
        </section>

        {/* Modal for Tracking */}
        {selectedOrder && (
          <div className="track-order-modal">
            <div className="modal-content">
              <h3>Tracking Order ID: {selectedOrder.id}</h3>
              <TrackOrder order={selectedOrder} />
              <button
                className="close-button"
                onClick={() => setSelectedOrder(null)}
              >
                Close
              </button>
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <section className="action-buttons">
          <Link to="/custheader/sellwaste">
            <button className="action-button">Sell new Waste</button>
          </Link>
          <Link to="/custheader/history">
            <button className="action-button">Previous Orders</button>
          </Link>
        </section>
      </div>
    </>
  );
};

export default FarmerDashboard;
