import React from 'react';
// import './Dashboard.module.css'; // Import the CSS module
// import WasteItem from './components/WasteItem';
// import ProcessingCard from './components/ProcessingCard';
// import ProductListingCard from './components/ProductListingCard';

// Dummy data
const dummyIntakeData = [
  { id: 1, type: 'Organic Waste', volume: 500, source: 'Local Farm', date: '2023-10-01' },
  { id: 2, type: 'Plastic Waste', volume: 300, source: 'Community Center', date: '2023-10-02' },
];

const dummyProcessingData = [
  { id: 1, type: 'Organic Waste', status: 'In Progress', estimatedCompletion: '2023-10-05' },
  { id: 2, type: 'Plastic Waste', status: 'Completed', estimatedCompletion: '2023-10-03' },
];

const dummyProductData = [
  { id: 1, name: 'Organic Compost', quantity: 200, price: 15 },
  { id: 2, name: 'Recycled Plastic', quantity: 100, price: 25 },
];

const Dashboard = () => {
  return (
    <div className="dashboard">
      <h1>Waste Processing Unit Dashboard</h1>
      <section>
        <h2>Recent Waste Intakes</h2>
        <div className="intake-list">
          {dummyIntakeData.map(item => (
            <WasteItem key={item.id} item={item} />
          ))}
        </div>
      </section>
      <section>
        <h2>Processing Status</h2>
        <div className="processing-list">
          {dummyProcessingData.map(item => (
            <ProcessingCard key={item.id} item={item} />
          ))}
        </div>
      </section>
      <section>
        <h2>Available Products</h2>
        <div className="product-list">
          {dummyProductData.map(item => (
            <ProductListingCard key={item.id} item={item} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default Dashboard;
