import React, { useRef, useState } from "react";
import "./SellWaste.css";

const SellWaste = () => {
  const formRefs = {
    name: useRef(null),
    email: useRef(null),
    mobileNo: useRef(null),
    address: useRef(null),
    wasteType: useRef(null),
    image: useRef(null),
  };

  const [message, setMessage] = useState("");
  const [imagePreview, setImagePreview] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Get the farmerId from local storage
    const farmerId = localStorage.getItem("userId");
    if (!farmerId) {
      setMessage("Error: Farmer ID not found. Please log in again.");
      return;
    }

    const formData = new FormData();
    formData.append("farmerid", farmerId);

    // Append form values to FormData
    Object.keys(formRefs).forEach((key) => {
      if (key === "image" && formRefs[key].current?.files[0]) {
        const file = formRefs[key].current.files[0];
        formData.append("imagePath", file, file.name); // Append file with its name
      } else {
        formData.append(key, formRefs[key].current.value);
      }
    });

    // Debugging: Log all form data entries
    for (let pair of formData.entries()) {
      console.log(pair[0] + ": " + pair[1]);
    }

    try {
      const response = await fetch("http://localhost:8085/waste-details/add", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();
      setMessage(response.ok ? result.message : `Error: ${result.message || "Unknown error"}`);
    } catch (error) {
      setMessage(`Error: ${error.message || "Unknown error"}`);
    }
  };

  return (
    <div className="sell-waste-container">
      <div className="form-card">
        <h1 className="form-title">Sell Your Waste</h1>
        <p className="form-subtitle">Turn your agricultural waste into profit</p>

        <form onSubmit={handleSubmit} className="waste-form">
          <div className="form-grid">
            <div className="form-group image-upload">
              <label className="custom-file-upload">
                <input
                  type="file"
                  ref={formRefs.image}
                  onChange={handleImageChange}
                  accept="image/*"
                />
                {imagePreview ? (
                  <img src={imagePreview} alt="Preview" className="image-preview" />
                ) : (
                  <div className="upload-placeholder">
                    <i className="fas fa-cloud-upload-alt"></i>
                    <span>Upload Waste Image</span>
                  </div>
                )}
              </label>
            </div>

            <div className="form-section">
              <div className="form-group">
                <label>Name</label>
                <input type="text" ref={formRefs.name} placeholder="Your full name" required />
              </div>

              <div className="form-group">
                <label>Email</label>
                <input type="email" ref={formRefs.email} placeholder="Your email address" required />
              </div>

              <div className="form-group">
                <label>Mobile Number</label>
                <input type="tel" ref={formRefs.mobileNo} placeholder="Your contact number" required />
              </div>
            </div>

            <div className="form-section">
              <div className="form-group">
                <label>Address</label>
                <textarea ref={formRefs.address} placeholder="Your detailed address" required />
              </div>

              <div className="form-group">
                <label>Waste Type</label>
                <select ref={formRefs.wasteType} required>
                  <option value="">Select waste type</option>
                  <option value="organic">Organic Waste</option>
                  <option value="crop">Crop Residue</option>
                  <option value="animal">Animal Waste</option>
                  <option value="other">Other</option>
                </select>
              </div>
            </div>
          </div>

          <button type="submit" className="submit-btn">
            Submit Request
          </button>
        </form>

        {message && <div className="message-toast">{message}</div>}
      </div>
    </div>
  );
};

export default SellWaste;
