import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import './History.css';

function History() {
  const [orders, setOrders] = useState([]);
  const [images, setImages] = useState({});
  const [rejectedOrders, setRejectedOrders] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    async function fetchData() {
      try {
        const intakeResponse = await axios.get('http://localhost:8085/intake/get');
        const fetchedOrders = intakeResponse.data;

        const wasteResponse = await axios.get(`http://localhost:8085/waste-details/orders/${userId}`);
        const wasteOrders = wasteResponse.data.filter(order => order.status === "COLLECTED" || order.status === "REJECTED");

        const wasteImagesMap = {};
        wasteOrders.forEach(order => {
          wasteImagesMap[order.id] = order.imagePath;
        });

        const matchedOrders = fetchedOrders.filter(order => wasteImagesMap[order.wasteId]);
        setOrders(matchedOrders);
        setImages(wasteImagesMap);

        const rejected = wasteOrders.filter(order => order.status === "REJECTED");
        setRejectedOrders(rejected);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    if (userId) {
      fetchData();
    }
  }, [userId]);

  const generateInvoice = (order) => {
    const doc = new jsPDF();

    doc.setFontSize(22);
    doc.text('FarmEco Invoice', 105, 20, { align: 'center' });
    doc.setFontSize(12);
    doc.text('Waste Selling Company', 105, 28, { align: 'center' });
    doc.line(10, 35, 200, 35);

    doc.setFontSize(10);
    doc.text('FarmEco Pvt Ltd.', 10, 45);
    doc.text('123 Greenfield Avenue, Pune, Maharashtra', 10, 50);
    doc.text('Contact: +91-1234567890 | Email: support@farmeco.com', 10, 55);

    const invoiceNumber = `FEC-${order.id.toString().padStart(6, '0')}-${order.wasteId}`;
    doc.setFontSize(12);
    doc.text(`Invoice Number: ${invoiceNumber}`, 140, 45);
    doc.text(`Date: ${new Date(order.collectedDate).toLocaleDateString()}`, 140, 50);

    doc.autoTable({
      startY: 70,
      head: [['Field', 'Value']],
      body: [
        ['Farmer Name:', order.farmerName],
        ['Place:', order.place],
        ['Collected By:', order.collectedBy]
      ],
      theme: 'striped',
    });

    doc.autoTable({
      startY: doc.lastAutoTable.finalY + 15,
      head: [['Item Description', 'Weight (tons)', 'Rate', 'Total Price']],
      body: [['Agricultural Waste', `${order.weight}`, `${order.rate}`, `${order.totalPrice}`]],
      theme: 'grid',
    });

    doc.save(`Invoice_${invoiceNumber}.pdf`);
  };

  return (
    <div className="order-history">
      <h2>Farmer Order History</h2>
      <div className="order-sections">
        <div className="order-container">
          <h3>Collected Orders</h3>
          <div className="card-container">
            {orders.map(order => (
              <div className="order-card" key={order.id}>
                <img
                  src={images[order.wasteId] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                  alt="Order Visual"
                  height="150px"
                  width="200px"
                />
                <p><strong>Order Date:</strong> {new Date(order.collectedDate).toLocaleDateString()}</p>
                <p><strong>Place:</strong> {order.place}</p>
                <p><strong>Weight:</strong> {order.weight} kg</p>
                <p><strong>Collected By:</strong> {order.collectedBy}</p>
                <p><strong>Farmer Name:</strong> {order.farmerName}</p>
                <p><strong>Rate:</strong> ₹{order.rate}</p>
                <p><strong>Total Price:</strong> ₹{order.totalPrice}</p>
                <button className="btn btn-danger" onClick={() => generateInvoice(order)}>Generate Invoice</button>
              </div>
            ))}
          </div>
        </div>
        
        <div className="order-container">
          <h3>Rejected Orders</h3>
          <div className="card-container">
            {rejectedOrders.length > 0 ? (
              rejectedOrders.map(order => (
                <div key={order.id} className="order-card rejected">
                  <h4>Order ID: ORD-{order.id}</h4>
                  <img
                    src={images[order.id] || "https://via.placeholder.com/200x150?text=No+Image+Available"}
                    height="150px"
                    width="200px"
                    alt={order.wasteType || "Product"}
                  />
                  <p>Waste Type: {order.wasteType || "Not specified"}</p>
                  <p>Status: <span className="order-status-text">{order.status}</span></p>
                </div>
              ))
            ) : (
              <p>No rejected orders available.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default History;
