import React from "react";
import Heading from "../common/heading/Heading";
import { awrapper } from "../../dummydata";
import "./awrapper.css";

const Awrapper = () => {
  const staticContent = [
    {
      title: "About Jai Kisan Agro",
      text: "Jai Kisan Agro is a forward-thinking agricultural company dedicated to transforming waste into health. Through innovative agricultural waste recycling techniques, we help reduce environmental impact while contributing to sustainable farming growth.",
    },
    {
      title: "Our Mission",
      text: "To reduce the harmful environmental impact of agricultural waste and promote a circular economy within the farming sector. By recycling agricultural waste, we create valuable resources that enhance soil health and improve crop yields.",
    },
    {
      title: "Our Vision",
      text: "We envision a world where farmers have access to eco-friendly solutions that improve crop health, increase productivity, and reduce waste. Jai Kisan Agro aims to be at the forefront of agricultural innovation.",
    },
    {
      title: "What We Do",
      text: "Agri Waste Recycling: Turning waste into compost, fertilizers, and biogas. Soil Health Improvement: Promoting organic solutions for healthier soils. Eco-Friendly Solutions: Offering biodegradable packaging and renewable energy. Farmer Empowerment: Supporting farmers with tools and knowledge.",
    },
    {
      title: "Our Values",
      text: "Sustainability: Transforming waste into resources while reducing pollution. Innovation: Constantly exploring new technologies. Empathy: Designing solutions to improve farmer efficiency and productivity.",
    },
    {
      title: "Why Choose Us?",
      text: "Environmentally Friendly: Reducing waste and pollution for a healthier environment. Support for Farmers: Providing sustainable alternatives for better crop yields. Quality Products: Ensuring high-quality compost, biogas, and more.",
    },
  ];

  return (
    <>
      <section className="awrapper">
        <div className="container grid">
          {awrapper.map((val) => (
            <div className="box flex" key={val.title}>
              <div className="img">
                <img src={val.cover} alt={val.title} />
              </div>
              <div className="text">
                <h1>{val.data}</h1>
                <h3>{val.title}</h3>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Static Content in Card Format */}
      <section className="content-cards">
        {staticContent.map((item, index) => (
          <div className="content-card" key={index}>
            <h2>{item.title}</h2>
            <p>{item.text}</p>
          </div>
        ))}
      </section>
    </>
  );
};

export default Awrapper;
