import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const FarmerDashboard = () => {
  const [farmer, setFarmer] = useState(null);
  const [orderStatus, setOrderStatus] = useState([]);
  const [completedOrders, setCompletedOrders] = useState([]);
  const [collectedRejectedOrders, setCollectedRejectedOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!userId) {
          console.error("No user ID found in localStorage");
          setLoading(false);
          return;
        }

        // Fetch Farmer Details
        const farmerResponse = await fetch(`http://localhost:8085/farmer/${userId}`);
        const farmerData = await farmerResponse.json();
        setFarmer(farmerData);

        // Fetch Orders
        const response = await fetch(`http://localhost:8085/waste-details/orders/${userId}`);
        const orders = await response.json();

        // Separate Orders by Status
        const pendingAccepted = orders.filter(
          (order) => ["ACCEPTED", "PENDING", null].includes(order.status)
        );
        const completed = orders.filter(order => order.status === "COLLECTED");
        const collectedRejected = orders.filter(order => ["COLLECTED", "REJECTED"].includes(order.status));

        setOrderStatus(pendingAccepted);
        setCompletedOrders(completed);
        setCollectedRejectedOrders(collectedRejected);

        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, [userId]);

  const handleLogout = () => {
    localStorage.removeItem("userId");
    alert("Logged out successfully!");
    window.location.href = "/";
  };

  const openTrackOrder = (order) => {
    setSelectedOrder(order);
  };

  if (loading) return <div>Loading...</div>;
  if (!farmer) return <div>Unable to load farmer data. Please try again later.</div>;

  return (
    <div className="farmer-dashboard">
      {/* Header Section */}
      <header className="dashboard-header">
        <div className="logo">Farm Dashboard</div>
        <div className="profile">
          <span className="p-3">{farmer.name}</span>
          <button className="logout-button" onClick={handleLogout}>Logout</button>
        </div>
      </header>

      {/* Farmer Information Section */}
      <section className="main-dashboard">
        <h2>Welcome, {farmer.name}!</h2>
        <div className="farmer-info">
          <p><strong>Id:</strong> F19050{farmer.id}</p>
          <p><strong>Name:</strong> {farmer.name}</p>
          <p><strong>Address:</strong> {farmer.address}</p>
          <p><strong>Email:</strong> {farmer.email}</p>
        </div>
        <Link to="/custheader/editprofile">
          <button type="button" className="btn btn-secondary">Edit Profile</button>
        </Link>
      </section>

      {/* 🚀 Pending & Accepted Orders Section */}
      <section className="order-status">
        <h3>Your Orders (Pending / Accepted)</h3>
        <div className="card-container">
          {orderStatus.length > 0 ? (
            orderStatus.map(order => (
              <div key={order.id} className={`card ${order.status ? order.status.toLowerCase() : "pending"}`}>
                <h4>Order ID: ORD-{order.id}</h4>
                <div className="d-flex justify-content-center align-items-center my-2">
                  <img
                    src={order.imagePath || "https://via.placeholder.com/200x150"}
                    height="150px"
                    width="200px"
                    alt={order.wasteType || "Product"}
                  />
                </div>
                <p><strong>Waste Type:</strong> {order.wasteType || "Not specified"}</p>
                <p><strong>Status:</strong> <span className="order-status-text">{order.status || "Pending"}</span></p>
                <button className="track-button" onClick={() => openTrackOrder(order)}>Track Order</button>
              </div>
            ))
          ) : (
            <p>No pending or accepted orders.</p>
          )}
        </div>
      </section>

      {/* 🚀 Completed Orders Section */}
      <section className="order-history">
        <h3>Completed Orders</h3>
        <div className="card-container">
          {completedOrders.length > 0 ? (
            completedOrders.map(order => (
              <div key={order.id} className="order-card">
                <h4>Order ID: ORD-{order.id}</h4>
                <img
                  src={order.imagePath || "https://via.placeholder.com/200x150"}
                  alt="Order"
                  width="200px"
                  height="150px"
                />
                <p><strong>Waste Type:</strong> {order.wasteType || "Not specified"}</p>
                <p><strong>Status:</strong> <span className="order-status-text">{order.status}</span></p>
              </div>
            ))
          ) : (
            <p>No completed orders available.</p>
          )}
        </div>
      </section>

      {/* 🚀 Collected & Rejected Orders Section */}
      <section className="order-status">
        <h3>Collected & Rejected Orders</h3>
        <div className="card-container">
          {collectedRejectedOrders.length > 0 ? (
            collectedRejectedOrders.map(order => (
              <div key={order.id} className={`card ${order.status.toLowerCase()}`}>
                <h4>Order ID: ORD-{order.id}</h4>
                <img
                  src={order.imagePath || "https://via.placeholder.com/200x150"}
                  height="150px"
                  width="200px"
                  alt="Order"
                />
                <p><strong>Waste Type:</strong> {order.wasteType || "Not specified"}</p>
                <p><strong>Status:</strong> <span className="order-status-text">{order.status}</span></p>
              </div>
            ))
          ) : (
            <p>No collected or rejected orders available.</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default FarmerDashboard;
